SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_motivo_alta_excl_proc ( cd_motivo_alta_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
nr_seq_proced_pac_w		bigint;
nr_seq_proc_interno_w	bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_motivo_exc_conta_w	smallint;

 
C01 CURSOR FOR 
	SELECT	cd_procedimento, 
			nr_seq_proc_interno, 
			ie_origem_proced, 
			cd_motivo_exc_conta 
	from	motivo_alta_excl_proc 
	where	cd_motivo_alta = cd_motivo_alta_p 
	and		coalesce(ie_situacao,'I') = 'A';

C02 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from	conta_paciente a, 
			procedimento_paciente b 
	where	a.nr_atendimento 		= nr_atendimento_p 
	and		b.nr_interno_conta 		= a.nr_interno_conta 
	and		((b.cd_procedimento 	= cd_procedimento_w AND b.ie_origem_proced 	= ie_origem_proced_w) or (b.nr_seq_proc_interno 	= nr_seq_proc_interno_w)) 
	and (coalesce(b.cd_motivo_exc_conta,0) = 0) 
	and		a.ie_status_acerto = 1;	
			 

BEGIN 
 
if (coalesce(cd_motivo_alta_p,0) > 0) and (coalesce(nr_atendimento_p,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		cd_procedimento_w, 
		nr_seq_proc_interno_w, 
		ie_origem_proced_w, 
		cd_motivo_exc_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (coalesce(cd_procedimento_w,0) > 0) or (coalesce(nr_seq_proc_interno_w,0) > 0) then				 
				open C02;
				loop 
				fetch C02 into	 
					nr_seq_proced_pac_w;
				EXIT WHEN NOT FOUND; /* apply on C02 */
					begin 
					if (coalesce(nr_seq_proced_pac_w,0) > 0) then 
						update	procedimento_paciente 
						set		cd_motivo_exc_conta = cd_motivo_exc_conta_w 
						where	nr_sequencia		= nr_seq_proced_pac_w;
					end if;					
					end;
				end loop;
				close C02;
		end if;
		end;
	end loop;
	close C01;
	 
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
