SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_nova_solic_compra ( cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_permite_nova_solic_w		varchar(1);
cd_perfil_w			bigint;
ds_erro_w			varchar(255) := '';
nr_solic_compra_w			bigint;


BEGIN

cd_perfil_w	:= obter_perfil_ativo;

select	coalesce(max(obter_valor_param_usuario(913, 205, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p)),'S')
into STRICT	ie_permite_nova_solic_w
;

if (ie_permite_nova_solic_w = 'N') then

	select	coalesce(max(nr_solic_compra),0)
	into STRICT	nr_solic_compra_w
	from	solic_compra
	where	cd_estabelecimento = cd_estabelecimento_p
	and	nm_usuario_nrec = nm_usuario_p
	and	coalesce(dt_baixa::text, '') = ''
	and	coalesce(dt_liberacao::text, '') = '';

	if (nr_solic_compra_w > 0) then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(280687) || nr_solic_compra_w || WHEB_MENSAGEM_PCK.get_texto(280688);
	end if;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
