CREATE OR REPLACE PROCEDURE consiste_novo_btn_click_rep_pt ( qt_min_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_usu_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
dt_alta_w			timestamp;
ie_fim_atend_w			varchar(1);
ie_situacao_medico_w		varchar(1);
ie_permite_medico_inativo_w	varchar(1) := 'N';

BEGIN
 
ie_permite_medico_inativo_w := obter_param_usuario(950, 82, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_medico_inativo_w);	
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	 
	if (qt_min_p <> 0) then 
		begin 
	 
		select (coalesce(dt_alta, clock_timestamp()) + qt_min_p /1440) 
		into STRICT	dt_alta_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
 
		if (dt_alta_w < clock_timestamp()) then 
			begin 
			--Tempo de geração de prescrição após alta do paciente, excedido! Parâmetro [43]. 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(82127);
			end;
		end if;
		end;
	end if;
	 
	select	coalesce(max(ie_situacao),'A') 
	into STRICT	ie_situacao_medico_w 
	from	medico 
	where	cd_pessoa_fisica = cd_pessoa_fisica_usu_p;
	 
	if (ie_situacao_medico_w = 'I') and (ie_permite_medico_inativo_w = 'N') then 
		begin 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(93417);
		end;
	end if;	
	 
	 
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_fim_atend_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and	(dt_fim_conta IS NOT NULL AND dt_fim_conta::text <> '');
	 
	if (ie_fim_atend_w = 'S') then 
		begin 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(82128);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
