SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_novo_ciclo ( cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_P INOUT text) AS $body$
DECLARE


ds_retorno_w		varchar(1000) := '';
qt_existe_w		integer := 0;
ie_consiste_ciclo_w varchar(1);


BEGIN

ie_consiste_ciclo_w := Obter_Param_Usuario(406, 219, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_ciclo_w);

select	count(*)
into STRICT	qt_existe_w
from	cm_ciclo
where	(dt_atualizacao_nrec IS NOT NULL AND dt_atualizacao_nrec::text <> '')
and	nm_usuario_nrec = nm_usuario_p
and	coalesce(dt_fim::text, '') = ''
and	coalesce(dt_cancelamento::text, '') = ''
and	dt_atualizacao_nrec > (clock_timestamp() - interval '10 days');

if (qt_existe_w > 0) then
	if (ie_consiste_ciclo_w = 'A') then
		ds_retorno_w := obter_texto_tasy(258467,null);
	elsif (ie_consiste_ciclo_w = 'S') then
		ds_retorno_w := obter_texto_tasy(258468,null);
	end if;
end if;

ds_retorno_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
