SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_ordem_compra_transf ( nr_ordem_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_item_oci_w				integer;
qt_material_w				double precision;
qt_material_entrega_w			double precision;
ds_erro_w				varchar(2000);
ds_erro_ww				varchar(2000) := ' ';
cd_item_erro_w				varchar(255) := '';
qt_item_compra_w				smallint;
vl_total_ordem_w				double precision;
vl_total_venc_w				double precision;
cd_material_w				integer;
ds_material_w				varchar(255);
cd_centro_custo_w				integer;
cd_local_estoque_w			smallint;
ie_aprovacao_ext_w			varchar(255);
qt_inativo_w				integer;
qt_condicao_inativo_w			smallint;
qt_reg_w					bigint;
cd_conta_contabil_w			varchar(20);
vl_minimo_nf_w				double precision;
qt_validade_vencida_w			integer;
cd_estabelecimento_w			smallint;
ie_consiste_validade_anvisa_w		varchar(1);
ie_consiste_valid_marca_mat_w		varchar(1);
ds_erro_validade_w				varchar(2000);
qt_dia_entrega_urgente_w			integer;
qt_entrega_w				integer;
nr_seq_marca_w				bigint;
dt_validade_anvisa_marca_w			timestamp;
nr_seq_subgrupo_compra_w			bigint;
ie_liberado_w				varchar(1);
ie_consiste_regra_w			varchar(1) := 'N';
ie_consiste_revisao_w			varchar(15);
ie_consiste_fornec_adiant_w			varchar(15);
ie_marcar_urgente_oc_w			varchar(1);
ie_forma_revisao_w				varchar(15);
nr_seq_licitacao_w				bigint;
nr_seq_reg_lic_item_w			bigint;

cd_cgc_fornecedor_w			varchar(14);
cd_centro_custo_ordem_w			integer;
cd_local_entrega_ordem_w			smallint;
ie_tipo_local_w				varchar(5);
Consiste_CCusto_Local_Direto_w 		varchar(1) := 'N';
Restringe_CCusto_Regra_w			varchar(1) := 'N';
Consiste_CCusto_Ordem_Item_w		varchar(1) := 'N';
ie_tipo_ordem_w				varchar(15);
qt_existe_regra_cc_usuario_w		smallint;
nr_seq_reg_preco_w			bigint;
ie_vigente_w				varchar(1);
nr_solic_compra_w				bigint;
qt_existe_w				bigint;
qt_forn_adiant_aberto_w			bigint;
ie_consiste_saldo_disp_atend_w		varchar(1) := 'N';
cd_estab_transf_w				smallint;
cd_local_transf_w				smallint;
qt_estoque_disp_atend_w			double precision;
cd_unidade_medida_compra_w		varchar(30);
qt_material_estoque_w			double precision;
ie_consiste_criterio_rateio_w			varchar(1);
ie_permite_itens_inativos_w			varchar(1);
ie_situacao_mat_w				varchar(1);
ie_consignado_w				varchar(1);
nr_seq_consistencia_w			bigint;
ie_vigente_anvisa_w 		material_estab.ie_vigente_anvisa%type;
ie_vigente_anvisa_marca_w	material_marca.ie_vigente_anvisa%type;

c01 CURSOR FOR 
SELECT	coalesce(a.cd_centro_custo,0), 
	coalesce(a.cd_local_estoque,0), 
	b.cd_material, 
	substr(adiciona_zeros_esquerda(to_char(a.nr_item_oci),5) || ' - (' || adiciona_zeros_esquerda(to_char(a.cd_material),6) || ') ' || b.ds_material,1,255), 
	a.nr_item_oci, 
	a.qt_material, 
	a.nr_seq_marca, 
	a.nr_seq_reg_lic_item, 
	a.cd_unidade_medida_compra, 
	coalesce(b.ie_situacao,'A') 
from	material b, 
	ordem_compra_item a 
where	a.cd_material		= b.cd_material 
and	a.nr_ordem_compra	= nr_ordem_p;
	

BEGIN 
delete	FROM ordem_compra_consist 
where	nr_ordem_compra = nr_ordem_p;
 
select	count(*) 
into STRICT	qt_item_compra_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_p;
 
select	coalesce(sum(vl_vencimento),0) vl_total_venc_w 
into STRICT	vl_total_venc_w 
from	ordem_compra_venc 
where	nr_ordem_compra = nr_ordem_p;
 
select	coalesce(sum(obter_valor_liquido_ordem(nr_ordem_compra)),0), 
	max(cd_estabelecimento), 
	coalesce(max(nr_seq_subgrupo_compra),0), 
	max(ie_tipo_ordem), 
	max(cd_local_entrega), 
	max(cd_centro_custo), 
	coalesce(max(cd_cgc_fornecedor),0) 
into STRICT	vl_total_ordem_w, 
	cd_estabelecimento_w, 
	nr_seq_subgrupo_compra_w, 
	ie_tipo_ordem_w, 
	cd_local_entrega_ordem_w, 
	cd_centro_custo_ordem_w, 
	cd_cgc_fornecedor_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_p;
 
Consiste_CCusto_Local_Direto_w 	:= 	Obter_Valor_Param_Usuario(146, 42, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
Restringe_CCusto_Regra_w 		:=	Obter_Valor_Param_Usuario(146, 43, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
Consiste_CCusto_Ordem_Item_w 	:=	Obter_Valor_Param_Usuario(146, 44, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
ie_consiste_saldo_disp_atend_w	:= 	Obter_Valor_Param_Usuario(146, 71, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
ie_consiste_criterio_rateio_w		:=	Obter_Valor_Param_Usuario(146, 68, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
ie_permite_itens_inativos_w		:= 	Obter_Valor_Param_Usuario(146, 83, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);
 
if (ie_consiste_saldo_disp_atend_w = 'S') then 
	begin 
	select	cd_estab_transf, 
		cd_local_transf 
	into STRICT	cd_estab_transf_w, 
		cd_local_transf_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_p;
	end;
end if;
 
select	coalesce(ie_consiste_validade_anvisa,'N'), 
	coalesce(ie_consiste_validade_marca_mat,'N'), 
	qt_dia_entrega_urgente 
into STRICT	ie_consiste_validade_anvisa_w, 
	ie_consiste_valid_marca_mat_w, 
	qt_dia_entrega_urgente_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
open c01;
loop 
fetch c01 into 
	cd_centro_custo_w, 
	cd_local_estoque_w, 
	cd_material_w, 
	ds_material_w, 
	nr_item_oci_w, 
	qt_material_w, 
	nr_seq_marca_w, 
	nr_seq_reg_lic_item_w, 
	cd_unidade_medida_compra_w, 
	ie_situacao_mat_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	/* Busca dados do material */
 
	select	ie_consignado 
	into STRICT	ie_consignado_w 
	from	material 
	where	cd_material = cd_material_w;
 
	if (cd_centro_custo_w	= 0) and (cd_local_estoque_w	= 0) then 
		begin 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279282),1,255);		
		 
		CALL gravar_ordem_compra_consist(nr_ordem_p, '02', ds_material_w, 'C', null, nm_usuario_p);
		end;
	end if;
	 
	if (ie_permite_itens_inativos_w = 'N') and (ie_situacao_mat_w <> 'A') then 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279283),1,255);
		 
		CALL gravar_ordem_compra_consist(nr_ordem_p, '13', ds_material_w, 'C', null, nm_usuario_p);
	end if;
	 
	if (Consiste_CCusto_Local_Direto_w = 'S') and (coalesce(cd_local_estoque_w,0) > 0) and (coalesce(cd_centro_custo_w,0) = 0) then 
		begin 
		select 	ie_tipo_local 
		into STRICT	ie_tipo_local_w 
		from	local_estoque 
		where	cd_local_estoque = cd_local_estoque_w;
		 
		if (ie_tipo_local_w = '8') then 
			begin 
			ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279285),1,255);
			CALL gravar_ordem_compra_consist(nr_ordem_p, '03', ds_material_w, 'C', null, nm_usuario_p);
			end;
		end if;
		end;
	end if;
	 
	if (Consiste_CCusto_Ordem_Item_w = 'S') and (coalesce(cd_centro_custo_w,0) > 0) and (coalesce(cd_centro_custo_ordem_w,0) > 0) and (coalesce(cd_centro_custo_w,0) <> coalesce(cd_centro_custo_ordem_w,0)) then 
		begin 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279286) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279287),1,255);
		CALL gravar_ordem_compra_consist(nr_ordem_p, '04', ds_material_w, 'C', null, nm_usuario_p);
		end;
	end if;
 
	if (Restringe_CCusto_Regra_w = 'S') and (coalesce(cd_centro_custo_w,0) > 0) then 
		begin 
		select	count(*) 
		into STRICT	qt_existe_regra_cc_usuario_w 
		from	sup_regra_centro_setor a 
		where	a.ie_situacao = 'A' 
		and	ie_transf_estab = 'S' 
		and	obter_se_usuario_setor(nm_usuario_p, a.cd_setor_atendimento) = 'S' 
		and	a.cd_centro_custo = cd_centro_custo_w;
		 
		if (qt_existe_regra_cc_usuario_w = 0) then 
			begin 
			ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279286) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279288),1,255);
			CALL gravar_ordem_compra_consist(nr_ordem_p, '05', ds_material_w, 'C', null, nm_usuario_p);
			end;
		end if;
		end;
	end if;
 
	select	coalesce(sum(qt_prevista_entrega), 0) 
	into STRICT	qt_material_entrega_w 
	from	ordem_compra_item_entrega 
	where	nr_ordem_compra	= nr_ordem_p 
	and	nr_item_oci	= nr_item_oci_w;
 
	if (qt_material_w	<> qt_material_entrega_w) then 
		begin 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279289),1,255);
		CALL gravar_ordem_compra_consist(nr_ordem_p, '06', ds_material_w, 'C', null, nm_usuario_p);
		end;
	end if;
	 
	select	max(qt_item) 
	into STRICT	qt_reg_w 
	from ( 
		SELECT	count(*) qt_item, 
			dt_prevista_entrega 
		from	ordem_compra_item_entrega 
		where	nr_ordem_compra = nr_ordem_p 
		and	nr_item_oci = nr_item_oci_w 
		and	coalesce(dt_cancelamento::text, '') = '' 
		group by dt_prevista_entrega) alias3;
 
	if (qt_reg_w > 1) then 
		begin 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279290),1,255); 	
		CALL gravar_ordem_compra_consist(nr_ordem_p, '07', ds_material_w, 'C', null, nm_usuario_p);
		end;
	end if;
	 
	/* consiste se validade do registro da anvisa vencida*/
 
	if (ie_consiste_validade_anvisa_w = 'S') then 
		 
		select	count(*) 
		into STRICT	qt_validade_vencida_w 
		from	material_estab 
		where	cd_material = cd_material_w 
		and		cd_estabelecimento = cd_estabelecimento_w 
		and		(dt_validade_reg_anvisa IS NOT NULL AND dt_validade_reg_anvisa::text <> '') 
		and		dt_validade_reg_anvisa < clock_timestamp();
		 
		select	max(coalesce(ie_vigente_anvisa,'N')) 
		into STRICT 	ie_vigente_anvisa_w 
		from	material_estab 
		where	cd_material 		= cd_material_w 
		and		cd_estabelecimento 	= cd_estabelecimento_w;
		 
		if (qt_validade_vencida_w > 0) and (ie_vigente_anvisa_w = 'N') then 
			begin 
			ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279291),1,255);
			CALL gravar_ordem_compra_consist(nr_ordem_p, '08', ds_material_w, 'C', null, nm_usuario_p);
			end;
		end if;
	end if;
	 
	/*Consiste a data de validade da marca do material*/
	 
	if (ie_consiste_valid_marca_mat_w = 'S') then 
 
		select	max(dt_validade_reg_anvisa), 
				max(ie_vigente_anvisa) 
		into STRICT	dt_validade_anvisa_marca_w, 
				ie_vigente_anvisa_marca_w 
		from	material_marca 
		where	cd_material = cd_material_w 
		and	nr_sequencia = nr_seq_marca_w;
		 
		if	((coalesce(ie_vigente_anvisa_marca_w,'X') = 'N') or (coalesce(dt_validade_anvisa_marca_w,clock_timestamp()) < trunc(clock_timestamp()))) then 
			begin 
			ds_erro_w :=	substr(WHEB_MENSAGEM_PCK.get_texto(279292) || substr(obter_desc_marca(nr_seq_marca_w),1,50) || WHEB_MENSAGEM_PCK.get_texto(279293) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279294),1,255);
			CALL gravar_ordem_compra_consist(nr_ordem_p, '09', ds_material_w, 'C', null, nm_usuario_p);
			end;
		end if;	
	end if;
 
	if (ie_consiste_saldo_disp_atend_w = 'S') then 
		begin 
		if (ie_consignado_w = '0') then -- Não consignado 
			begin 
			qt_estoque_disp_atend_w := coalesce(obter_saldo_disp_estoque(cd_estab_transf_w,cd_material_w,cd_local_transf_w,trunc(clock_timestamp(),'mm')),0);
			qt_material_estoque_w	:= coalesce(obter_quantidade_convertida(cd_material_w,qt_material_w,cd_unidade_medida_compra_w,'UME'),0);
			if (qt_estoque_disp_atend_w < qt_material_estoque_w) then 
				begin 
				ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279295),1,255);
				CALL gravar_ordem_compra_consist(nr_ordem_p, '10', ds_material_w, 'C', null, nm_usuario_p);
				end;
			end if;
			end;
		end if;	
		end;
	end if;
 
	/*consistir dados na material_estab*/
 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	material_estab 
	where	cd_material		= cd_material_w 
	and	cd_estabelecimento 	= cd_estabelecimento_w;
 
	if (qt_reg_w = 0) then 
		begin 
		ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279281) || substr(ds_material_w,1,100) || WHEB_MENSAGEM_PCK.get_texto(279296),1,255);
		CALL gravar_ordem_compra_consist(nr_ordem_p, '11', ds_material_w, 'C', null, nm_usuario_p);
		end;
	end if;	
	end;
end loop;
close c01;
 
select	coalesce(max(qt),0) 
into STRICT	qt_reg_w 
from (SELECT	count(*) qt, 
		trunc(dt_vencimento,'dd') 
	from	ordem_compra_venc 
	where	nr_ordem_compra = nr_ordem_p 
	GROUP BY trunc(dt_vencimento,'dd')) alias5;
 
if (qt_reg_w > 0 HAVING	count(trunc(dt_vencimento,'dd')) > 1 
	) then 
	begin 
	ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(279298),1, 255);
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
if (qt_dia_entrega_urgente_w IS NOT NULL AND qt_dia_entrega_urgente_w::text <> '') then 
	begin 
	select	count(*) 
	into STRICT	qt_entrega_w 
	FROM ordem_compra_item a
LEFT OUTER JOIN ordem_compra_item_entrega b ON (a.nr_ordem_compra = b.nr_ordem_compra AND a.nr_item_oci = b.nr_item_oci)
WHERE dt_prevista_entrega	<= trunc(clock_timestamp()) + qt_dia_entrega_urgente_w and a.nr_ordem_compra 	= nr_ordem_p;
 
	if (qt_entrega_w > 0) and (ie_marcar_urgente_oc_w = 'S') then 
		update	ordem_compra 
		set	ie_urgente = 'S' 
		where	nr_ordem_compra = nr_ordem_p;
	end if;
	end;	
end if;
 
if (qt_item_compra_w = 0) then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279299);
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
if (Consiste_CCusto_Local_Direto_w = 'S') and (coalesce(cd_local_entrega_ordem_w,0) > 0) and (coalesce(cd_centro_custo_ordem_w,0) = 0) then 
	begin 
	select 	ie_tipo_local 
	into STRICT	ie_tipo_local_w 
	from	local_estoque 
	where	cd_local_estoque = cd_local_entrega_ordem_w;
	 
	if (ie_tipo_local_w = '8') then 
		begin 
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279300);
		CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
		end;
	end if;
	end;
end if;
 
if (Restringe_CCusto_Regra_w = 'S') and (coalesce(cd_centro_custo_ordem_w,0) > 0) then 
	begin 
	select	count(*) 
	into STRICT	qt_existe_regra_cc_usuario_w 
	from	sup_regra_centro_setor a 
	where	a.ie_situacao = 'A' 
	and	ie_transf_estab = 'S' 
	and	obter_se_usuario_setor(nm_usuario_p, a.cd_setor_atendimento) = 'S' 
	and	a.cd_centro_custo = cd_centro_custo_ordem_w;
	 
	if (qt_existe_regra_cc_usuario_w = 0) then 
		begin 
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279301);
		CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
		end;
	end if;
	end;
end if;
 
if (vl_total_ordem_w <> vl_total_venc_w)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279302);
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*) 
into STRICT	qt_inativo_w 
from	pessoa_juridica b, 
	ordem_compra a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	a.cd_cgc_fornecedor	= b.cd_cgc 
and	b.ie_situacao		<> 'A';
 
if (qt_inativo_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279303);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*) 
into STRICT	qt_condicao_inativo_w 
from	condicao_pagamento b, 
	ordem_compra a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	a.cd_condicao_pagamento	= b.cd_condicao_pagamento 
and	b.ie_situacao		<> 'A';
 
if (qt_condicao_inativo_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279304);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*) 
into STRICT	qt_reg_w 
from	ordem_compra a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	(cd_cgc_fornecedor IS NOT NULL AND cd_cgc_fornecedor::text <> '') 
and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');
 
if (qt_reg_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279305);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*) 
into STRICT	qt_reg_w 
from	ordem_compra a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	coalesce(cd_cgc_fornecedor::text, '') = '' 
and	coalesce(cd_pessoa_fisica::text, '') = '';
 
if (qt_reg_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279307);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*), 
	max(a.cd_conta_contabil) 
into STRICT	qt_reg_w, 
	cd_conta_contabil_w 
from	conta_contabil b, 
	ordem_compra_item a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	a.cd_conta_contabil	= b.cd_conta_contabil 
and	b.ie_tipo		= 'T';	
 
if (qt_reg_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279309) || cd_conta_contabil_w || WHEB_MENSAGEM_PCK.get_texto(279310);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	coalesce(max(a.cd_conta_contabil), 'X') 
into STRICT	cd_conta_contabil_w 
from	ordem_compra b, 
	ordem_compra_item a 
where	a.nr_ordem_compra	= b.nr_ordem_compra 
and	a.nr_ordem_compra	= nr_ordem_p 
and (substr(obter_se_conta_vigente(a.cd_conta_contabil, b.dt_ordem_compra),1,1) = 'N');
 
if (cd_conta_contabil_w <> 'X') then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279309) || cd_conta_contabil_w || WHEB_MENSAGEM_PCK.get_texto(279311);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
select	count(*), 
	max(a.cd_centro_custo) 
into STRICT	qt_reg_w, 
	cd_centro_custo_w 
from	centro_custo b, 
	ordem_compra_item a 
where	a.nr_ordem_compra	= nr_ordem_p 
and	a.cd_centro_custo	= b.cd_centro_custo 
and	b.ie_tipo		= 'T';
	 
if (qt_reg_w > 0)	then 
	begin 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279313) || cd_centro_custo_w || WHEB_MENSAGEM_PCK.get_texto(279314);	
	CALL gravar_ordem_compra_consist(nr_ordem_p, '01', ds_erro_w, 'C', null, nm_usuario_p);
	end;
end if;
 
if (ie_consiste_criterio_rateio_w = 'S') then 
	begin 
	ds_erro_ww := consiste_crit_rateio_oc_transf(nr_ordem_p, nm_usuario_p, ds_erro_ww);
	if (ds_erro_ww <> '') then 
		ds_erro_w := ds_erro_ww;
	end if;
	end;
end if;
 
CALL sup_consistir_consumo_req(nr_ordem_p, null, 'TR', nm_usuario_p);
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_seq_consistencia_w 
from	ordem_compra_consist 
where	nr_ordem_compra = nr_ordem_p 
and	ie_tipo_consistencia = '14';
 
if (nr_seq_consistencia_w > 0) then 
	select	ds_consistencia 
	into STRICT	ds_erro_w 
	from	ordem_compra_consist 
	where	nr_Sequencia = nr_seq_consistencia_w;
end if;
	 
ds_erro_p := substr(ds_erro_w,1,255);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_ordem_compra_transf ( nr_ordem_p text, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

