SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_ordem_serv_vinculada ( nr_seq_superior_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

ds_retorno_w		varchar(255);
qt_quantidade_w	integer;
nr_sequencia_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	man_ordem_servico
where	nr_seq_superior = nr_seq_superior_p
and	ie_status_ordem <> '3';


BEGIN
select	count(nr_sequencia)
into STRICT	qt_quantidade_w
from	man_ordem_servico
where	nr_seq_superior = nr_seq_superior_p
and	ie_status_ordem <> '3';

IF qt_quantidade_w > 0 then
	OPEN C01;
	LOOP
		FETCH C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_retorno_w := nr_sequencia_w || ';' || ds_retorno_w;
		end;

		ds_retorno_p := 'Esta ordem de serviço não pode ser encerrada!' || chr(13) ||
				'Existem ordens vinculadas: ' || ds_retorno_w;

	END LOOP;
	CLOSE C01;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
