SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_perda_ganho ( cd_pessoa_fisica_p text, vl_item_p bigint, nr_seq_item_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


vl_minimo_per_w		double precision;
vl_maximo_per_w		double precision;
ds_perda_ganho_w	varchar(60);
qt_idade_w		double precision;

c01 CURSOR FOR
	SELECT	b.vl_minimo,
		b.vl_maximo,
		a.ds_tipo
	from	tipo_perda_ganho_limite b,
		tipo_perda_ganho a
	where	a.nr_sequencia	= nr_seq_item_p
	and	a.nr_sequencia	= b.nr_seq_tipo_pg
	and (qt_idade_w  between b.qt_idade_min and b.qt_idade_max);


BEGIN

select	max(obter_idade(dt_nascimento,clock_timestamp(),'A'))
into STRICT	qt_idade_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

open C01;
loop
fetch C01 into
	vl_minimo_per_w,
	vl_maximo_per_w,
	ds_perda_ganho_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ds_retorno_p := '';
if (not(vl_item_p between vl_minimo_per_w and vl_maximo_per_w)) then
	/*
	ds_retorno_p :=	'Valor inválido!'||chr(13)||chr(10)||
			'Há uma regra cadastrada para o Ganho/Perda ' || ds_perda_ganho_w || chr(13)||chr(10)||
			'que limita os valores entre '||to_char(vl_minimo_per_w) || ' e ' || to_char(vl_maximo_per_w);
	*/
	ds_retorno_p := wheb_mensagem_pck.get_texto(300516, 'DS_PERDA='||ds_perda_ganho_w||';VL_MIN='||to_char(vl_minimo_per_w)||';VL_MAX='||to_char(vl_maximo_per_w));

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_perda_ganho ( cd_pessoa_fisica_p text, vl_item_p bigint, nr_seq_item_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

