CREATE OR REPLACE PROCEDURE consiste_permissao_gerar_op ( nr_seq_opm_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

cd_material_w		bigint;
cd_perfil_w		integer;
cd_pessoa_fisica_w	varchar(10);
cd_setor_atendimento_w	integer;
ie_permite_w		varchar(1);
ie_permite_ww		varchar(1) := 'S';

C01 CURSOR FOR 
	SELECT	coalesce(ie_permite,'S') 
	from	regra_permissao_op_opm 
	where	coalesce(cd_estabelecimento,cd_estabelecimento_p) 		= cd_estabelecimento_p 
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_w) 		= cd_pessoa_fisica_w 
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w 
	and	coalesce(cd_perfil,cd_perfil_w)				= cd_perfil_w 
	and	coalesce(cd_material,cd_material_w)				= cd_material_w 
	order by	coalesce(cd_estabelecimento,0) desc, 
			coalesce(cd_perfil,0) desc, 
			coalesce(cd_setor_atendimento,0) desc, 
			coalesce(cd_pessoa_fisica,0) desc, 
			coalesce(cd_material,0);

 
 

BEGIN 
cd_perfil_w		:= wheb_usuario_pck.get_cd_perfil;
cd_setor_atendimento_w	:= wheb_usuario_pck.get_cd_setor_atendimento;
cd_pessoa_fisica_w	:= obter_pessoa_fisica_usuario(nm_usuario_p,'C');
 
select	max(cd_material) 
into STRICT	cd_material_w 
from	prescr_opm 
where	nr_sequencia = 	nr_seq_opm_p;
 
open C01;
loop 
fetch C01 into	 
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_permite_ww := ie_permite_w;
	end;
end loop;
close C01;
 
if (ie_permite_ww = 'N') then 
	-- Não é possível gerar a ordem de produção desta OPM. Verificar a regra "Permissão da geração da ordem de produção de OPM". 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(362915);
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
