SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_prescr_cirurgia ( NR_PRESCRICAO_P bigint, DS_CONSISTE_P INOUT text) AS $body$
DECLARE

dt_liberacao_w		timestamp;
dt_baixa_w		timestamp;
dt_liberacao_medico_w	timestamp;


BEGIN

if (nr_prescricao_p > 0) then

	select	dt_liberacao,
		dt_baixa,
		dt_liberacao_medico
	into STRICT	dt_liberacao_w,
		dt_baixa_w,
		dt_liberacao_medico_w
	from	prescr_medica
	where	nr_prescricao		= nr_prescricao_p;

	if 	not(coalesce(dt_liberacao_w::text, '') = '')		then
		ds_consiste_p := Wheb_mensagem_pck.get_texto(306388); -- 'Esta prescrição foi liberada pela farmácia';
	elsif	not(coalesce(dt_baixa_w::text, '') = '')		then
		ds_consiste_p := Wheb_mensagem_pck.get_texto(306389); -- 'Esta prescrição foi baixada';
	elsif	not(coalesce(dt_liberacao_medico_w::text, '') = '')	then
		ds_consiste_p := Wheb_mensagem_pck.get_texto(306390); -- 'Esta prescrição foi liberada ou baixada';
	end if;

end	if;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_prescr_cirurgia ( NR_PRESCRICAO_P bigint, DS_CONSISTE_P INOUT text) FROM PUBLIC;

