SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_proc_orc ( nr_seq_orcamento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_autorizacao_p INOUT text, nr_seq_proc_interno_p bigint, ie_regra_p INOUT text) AS $body$
DECLARE

				
nr_atendimento_w		bigint;				
dt_orcamento_w			timestamp;
ie_tipo_atendimento_w		bigint;
cd_convenio_w			bigint;
cd_categoria_w			varchar(20);
cd_plano_w			varchar(10);
ie_glosa_w			varchar(20);
ds_retorno_w			varchar(200);
nr_retorno_w			double precision;
ie_regra_w			varchar(10);
ie_consiste_glosa_partic_w 	varchar(10);
ie_consiste_regra_plano_w	varchar(10);
ie_resp_autor_w			varchar(10);
nr_seq_regra_w			bigint;
ie_consiste_edicao_w		varchar(10);
ie_proc_edicao_w		varchar(1);
ie_proc_pacote_w		varchar(1);
ie_glosa_edicao_w		varchar(1) := 'N';
cd_pessoa_fisica_w		varchar(10);
ie_glosa_plano_w		regra_ajuste_proc.ie_glosa%type;
nr_seq_regra_preco_w		regra_ajuste_proc.nr_sequencia%type;


BEGIN


ie_consiste_glosa_partic_w := Obter_Param_Usuario(106, 44, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_glosa_partic_w);
ie_consiste_regra_plano_w := Obter_Param_Usuario(106, 28, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_regra_plano_w);
ie_consiste_edicao_w := Obter_Param_Usuario(106, 36, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_edicao_w);

select 	coalesce(nr_atendimento,0),
	dt_orcamento,
	coalesce(ie_tipo_atendimento,0),
	cd_convenio,
	cd_categoria,
	cd_plano,
	cd_pessoa_fisica
into STRICT	nr_atendimento_w,
	dt_orcamento_w,
	ie_tipo_atendimento_w,
	cd_convenio_w,	
	cd_categoria_w,
	cd_plano_w,
	cd_pessoa_fisica_w
from	orcamento_paciente
where	nr_sequencia_orcamento  =  nr_seq_orcamento_p;

if (ie_consiste_edicao_w <> 'N') then
	
	select	coalesce(obter_se_proc_conv(cd_estabelecimento_p, cd_convenio_w, cd_categoria_w, dt_orcamento_w, cd_procedimento_p, ie_origem_proced_p,
			nr_seq_proc_interno_p, ie_tipo_atendimento_w), 'N'),
		coalesce(obter_se_pacote_convenio(cd_procedimento_p, ie_origem_proced_p, cd_convenio_w, cd_estabelecimento_p), 'N')
	into STRICT	ie_proc_edicao_w,
		ie_proc_pacote_w
	;
	
	if (ie_proc_edicao_w = 'N') and (ie_proc_pacote_w = 'N') then
		ie_glosa_edicao_w	:= 'S';
	end if;
	
end if;

if (ie_consiste_glosa_partic_w = 'S') then

	SELECT * FROM glosa_procedimento(	cd_estabelecimento_p, nr_atendimento_w, dt_orcamento_w, cd_procedimento_p, ie_origem_proced_p, qt_procedimento_p, 0, ie_tipo_atendimento_w, 0, 0, nr_seq_proc_interno_p, null, cd_plano_w, 0, 0, null, null, cd_convenio_w, cd_categoria_w, nr_retorno_w, ds_retorno_w, ds_retorno_w, nr_retorno_w, nr_retorno_w, ds_retorno_w, ds_retorno_w, ie_glosa_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w, null, null, null, null, 0, null, null, null, null, null, null, null, null) INTO STRICT cd_convenio_w, cd_categoria_w, nr_retorno_w, ds_retorno_w, ds_retorno_w, nr_retorno_w, nr_retorno_w, ds_retorno_w, ds_retorno_w, ie_glosa_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w, ds_retorno_w, nr_retorno_w;
end if;

if (ie_glosa_w in ('T','D','F','G')) or (ie_glosa_edicao_w = 'S') then --(ie_glosa_w <> 'L') then
	ie_autorizacao_p	:= 'B';
	ie_regra_p		:= 0;
elsif (coalesce(cd_plano_w, '0') <> '0') and (ie_consiste_regra_plano_w = 'S' or
	ie_consiste_regra_plano_w = 'M') then
	
	
	SELECT * FROM consiste_plano_convenio(nr_atendimento_w, cd_convenio_w, cd_procedimento_p, ie_origem_proced_p, clock_timestamp(), qt_procedimento_p, ie_tipo_atendimento_w, cd_plano_w, null, ds_retorno_w, null, null, ie_regra_w, null, nr_seq_regra_w, nr_seq_proc_interno_p, cd_categoria_w, cd_estabelecimento_p, null, null, cd_pessoa_fisica_w, ie_glosa_plano_w, nr_seq_regra_preco_w) INTO STRICT ds_retorno_w, ie_regra_w, nr_seq_regra_w, ie_glosa_plano_w, nr_seq_regra_preco_w;				
				
			
					
	if (ie_regra_w in (1,2,5,8)) then
		ie_autorizacao_p	:= 'B';
	end if;
	
	if (ie_regra_w = 4) then
		ie_autorizacao_p := 'L';
	end if;

	if (ie_regra_w in (3,6,7)) then
		select 	coalesce(max(ie_resp_autor),'H')
		into STRICT	ie_resp_autor_w
		from 	regra_convenio_plano
		where 	nr_sequencia = nr_seq_regra_w;
		if (ie_resp_autor_w = 'H') then
			ie_autorizacao_p 	:= 'PAH'; --Pendente de autoriza? (Guia Hospital)
		end if;
		if (ie_resp_autor_w = 'P') then
			ie_autorizacao_p 	:= 'PAP'; --Pendente de autoriza? (Guia Paciente)
		end if;
	end if;
		
	ie_regra_p	:= ie_regra_w;
end if;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_proc_orc ( nr_seq_orcamento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_autorizacao_p INOUT text, nr_seq_proc_interno_p bigint, ie_regra_p INOUT text) FROM PUBLIC;

