CREATE OR REPLACE PROCEDURE consiste_projetos_desenv_tec () AS $body$
DECLARE


nr_seq_proj_w	bigint;

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	proj_projeto
	where (trunc(dt_inicio_prev) >= to_date('01/06/2010', 'dd/mm/yyyy')
	or	trunc(dt_inicio_real) >= to_date('01/06/2010', 'dd/mm/yyyy'))
	and	nr_seq_estagio <> 1
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_proj_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL consiste_projeto_desenv_tec(nr_seq_proj_w);

	end;
end loop;
close C01;

end;
