SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_pront_transf_pendente ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, dt_periodo_inicial_p timestamp) AS $body$
DECLARE


qt_recebimento_w		bigint:= 0;
nr_prontuario_w			bigint;
qt_pront_retirado_w		bigint:= 0;
qt_pront_em_caixa_w		bigint:= 0;
qt_pront_outro_setor_w		bigint:= 0;
ie_permite_transf_retir_w	varchar(1);
ie_controla_transf_w		varchar(1);
ie_permite_transf_caixa_w	varchar(1);
ie_transf_outros_setores_w	varchar(1);


BEGIN

ie_controla_transf_w 		:=  obter_valor_param_usuario(941, 53, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
ie_permite_transf_retir_w 	:=  obter_valor_param_usuario(941, 153, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
ie_permite_transf_caixa_w 	:=  obter_valor_param_usuario(941, 154, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
ie_transf_outros_setores_w 	:=  obter_valor_param_usuario(941, 166, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	if (ie_transf_outros_setores_w <> 'S') and (ie_controla_transf_w = 'N') then
		select	count(*)
		into STRICT	qt_pront_outro_setor_w
		from	same_prontuario
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	((nr_atendimento_p 	= 0) or (nr_atendimento	= nr_atendimento_p))
		and	cd_setor_atendimento	<> wheb_usuario_pck.get_cd_setor_atendimento
		and	((ie_transf_outros_setores_w = 'N') or (ie_transf_outros_setores_w = 'U' and obter_se_setor_usuario(cd_setor_atendimento, wheb_usuario_pck.get_nm_usuario) <> 'S'));


		if (qt_pront_outro_setor_w > 0) then
			--Não é permitido realizar transferência de prontuários que estejam em um setor diferente do setor do usuário.  Parâmetro 166.
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176985);
		end if;
	end if;

	if (ie_permite_transf_caixa_w = 'N') and (ie_controla_transf_w = 'N') then
		select	count(*)
		into STRICT	qt_pront_em_caixa_w
		from	same_prontuario
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	((nr_atendimento_p 	= 0) or (nr_atendimento	= nr_atendimento_p))
		and	(nr_seq_caixa IS NOT NULL AND nr_seq_caixa::text <> '');

		if (qt_pront_em_caixa_w > 0) then
			--Não é permitido realizar transferência de prontuários armazenados em caixa. Parâmetro 154.
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176990);
		end if;
	end if;

	if (ie_permite_transf_retir_w = 'N') and (ie_controla_transf_w = 'N') then
		select	count(*)
		into STRICT	qt_pront_retirado_w
		from	same_prontuario
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	((nr_atendimento_p 	= 0) or (nr_atendimento	= nr_atendimento_p))
		and	ie_status in ('3','4','7');

		if (qt_pront_retirado_w > 0) then
			--Existem prontuários retirados pelo médico, funcionário ou paciente, é necessário que os mesmos sejam devolvidos. Parâmetro 153.
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176991);
		end if;
	end if;

	select	count(*)
	into STRICT	qt_recebimento_w
	from	transf_prontuario
	where	coalesce(dt_recebimento::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = ''
	and	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	--and	((dt_periodo_inicial is null) or (dt_periodo_inicial	= dt_periodo_inicial_p))
	and	((nr_atendimento_p 	= 0) or (nr_atendimento	= nr_atendimento_p));

	select	max(nr_prontuario)
	into STRICT	nr_prontuario_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (qt_recebimento_w > 0) then
		--Não será possível transferir este prontuário #@NR_PRONTUARIO#@ para outro setor, pois o mesmo está com data de recebimento pendente.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(176994,'NR_PRONTUARIO='||nr_prontuario_w);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_pront_transf_pendente ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, dt_periodo_inicial_p timestamp) FROM PUBLIC;

