SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_pront_transf_pend_js ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, dt_periodo_inicial_p timestamp, ie_consiste_p text) AS $body$
DECLARE


qt_recebimento_w	bigint:= 0;
nr_prontuario_w		bigint;
ie_retorno_w		varchar(1) := '';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_recebimento_w
	from	transf_prontuario
	where	coalesce(dt_recebimento::text, '') = ''
	and	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	--and	((dt_periodo_inicial is null) or (dt_periodo_inicial	= dt_periodo_inicial_p))
	and	((nr_atendimento_p 	= 0) or (nr_atendimento	= nr_atendimento_p));

	select	max(nr_prontuario)
	into STRICT	nr_prontuario_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (qt_recebimento_w > 0) then
		--Não será possível transferir este prontuário '|| nr_prontuario_w ||' para outro setor, pois o mesmo está com data de recebimento pendente. #@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261453, 'PRONTUARIO='||to_char(nr_prontuario_w));
	end if;

end if;

if (ie_consiste_p = 'N') then
	ie_retorno_w := obter_se_pront_atend_same(nr_atendimento_p);

	if (ie_retorno_w = 'S') then
		--O atendimento' || nr_atendimento_p ||' já tem o prontuário arquivado no SAME, por isso não permite transferência entre setores. Parâmetro[13].'||'#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261457, 'ATENDIMENTO='||to_char(nr_atendimento_p));
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
