SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_qtd_agend_permitidos ( nr_seq_agenda_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

ie_consiste_w			varchar(1):='N';
dt_agenda_w				timestamp;
dt_agenda_hora_w		timestamp;
hr_inicio_w				timestamp;
hr_fim_regra_w			timestamp;
hr_inicio_regra_w		timestamp;
cd_agenda_w				bigint;
cd_agenda_regra_w		bigint;
qt_agendamento_regra_w	bigint;
qt_agendamento_w		bigint;
nr_sequencia_w			bigint;
ds_erro_w				varchar(2000):='';



BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_consiste_w
from	qtdade_agendamento_periodo
where	ie_situacao = 'A';

if (ie_consiste_w = 'S') then
	begin
	select	max(to_date(to_char(dt_agenda,'dd/mm/yyyy') || ' ' || to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')),
			max(dt_agenda),
			max(cd_agenda),
			max(hr_inicio)
	into STRICT	dt_agenda_hora_w,
			dt_agenda_w,
			cd_agenda_w,
			hr_inicio_w
	from 	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	select	coalesce(max(nr_sequencia),0),
			coalesce(max(qt_agendamento),0),
			coalesce(max(cd_agenda),0),
			coalesce(max(to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')),null),
			coalesce(max(to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_fim,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')),null)
	into STRICT	nr_sequencia_w,
			qt_agendamento_regra_w,
			cd_agenda_regra_w,
			hr_inicio_regra_w,
			hr_fim_regra_w
	from 	qtdade_agendamento_periodo
	where	coalesce(cd_agenda,cd_agenda_w) = cd_agenda_w
	and		ie_situacao = 'A'
	and		hr_inicio_w between
			to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and
			to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_fim,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');
	if (nr_sequencia_w > 0)	then
		begin
		select	count(*)
		into STRICT	qt_agendamento_w
		from	agenda_paciente
		where	((cd_agenda_regra_w = 0) or (cd_agenda = cd_agenda_regra_w))
		and		hr_inicio between
				to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_inicio_regra_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') and
				to_date(to_char(dt_agenda_w,'dd/mm/yyyy') || ' ' || to_char(hr_fim_regra_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		and		(dt_agendamento IS NOT NULL AND dt_agendamento::text <> '');
		if (qt_agendamento_w >= qt_agendamento_regra_w) then
			ds_erro_w:=	wheb_mensagem_pck.get_texto(278149);
		end if;
		end;
	end if;
	end;
end if;

ds_erro_p := ds_erro_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
