SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_qt_maior_padrao_local ( cd_material_p bigint, qt_atendida_p bigint, nr_requisicao_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_estoque_max_w		double precision;
qt_material_transf_w		double precision;
qt_estoque_disp_w		double precision;
qt_saldo_local_transf_w		double precision;
cd_local_estoque_w		smallint;
cd_local_estoque_dest_w		smallint;
ie_entrada_saida_w		varchar(1);
ie_tipo_requisicao_w		varchar(3);
cd_unid_medida_w		item_requisicao_material.cd_unidade_medida%type;
ie_requisicao_transferencia_w	varchar(1) := 'N';
cd_operacao_estoque_w			operacao_estoque.cd_operacao_estoque%type;


BEGIN

select	a.cd_local_estoque,
	a.cd_local_estoque_destino,
	b.ie_entrada_saida,
	b.ie_tipo_requisicao,
	a.cd_operacao_estoque
into STRICT	cd_local_estoque_w,
	cd_local_estoque_dest_w,
	ie_entrada_saida_w,
	ie_tipo_requisicao_w,
	cd_operacao_estoque_w
from	operacao_estoque b,
	requisicao_material a
where	a.cd_operacao_estoque	= b.cd_operacao_estoque
and	a.nr_requisicao		= nr_requisicao_p;

/* Tratamento transferencia entre locais Mexico */

if (ie_tipo_requisicao_w in ('2','21')) then
	ie_requisicao_transferencia_w := 'S';
else
	ie_requisicao_transferencia_w := obter_se_transf_local_mexico(
							cd_estabelecimento_p	=> cd_estabelecimento_p,
							cd_operacao_estoque_p	=> cd_operacao_estoque_w);
end if;

if (ie_requisicao_transferencia_w = 'S') and (coalesce(qt_atendida_p,0) > 0) then
	begin

	select	a.cd_local_estoque,
		a.cd_local_estoque_destino,
		b.ie_entrada_saida
	into STRICT	cd_local_estoque_w,
		cd_local_estoque_dest_w,
		ie_entrada_saida_w
	from	operacao_estoque b,
		requisicao_material a
	where	a.cd_operacao_estoque	= b.cd_operacao_estoque
	and	a.nr_requisicao		= nr_requisicao_p;

	if (ie_entrada_saida_w = 'E') then
		cd_local_estoque_dest_w	:= cd_local_estoque_w;
	end if;
		
	select	coalesce(max(qt_estoque_maximo),0)
	into STRICT	qt_estoque_max_w
	from	padrao_estoque_local
	where	cd_material		= cd_material_p
	and	cd_local_estoque		= cd_local_estoque_dest_w
	and	cd_estabelecimento	= cd_estabelecimento_p;

	if (qt_estoque_max_w > 0) then
		begin
		
        select coalesce(max(cd_unidade_medida), obter_dados_material(cd_material_p,'UMC'))
        into STRICT cd_unid_medida_w
        from item_requisicao_material
        where nr_requisicao = nr_requisicao_p
        and cd_material = cd_material_p;

		qt_material_transf_w	:= obter_quantidade_convertida(cd_material_p, qt_atendida_p, cd_unid_medida_w, 'UME');
		qt_estoque_disp_w	:= obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_p, cd_local_estoque_dest_w, clock_timestamp());

		qt_saldo_local_transf_w	:= (coalesce(qt_estoque_disp_w,0) + coalesce(qt_material_transf_w,0));

		if (qt_saldo_local_transf_w > qt_estoque_max_w) then
			ds_erro_p := 	WHEB_MENSAGEM_PCK.get_texto(281688) || chr(13) || chr(10) ||
					WHEB_MENSAGEM_PCK.get_texto(281689) || qt_estoque_disp_w || chr(13) || chr(10) ||
					WHEB_MENSAGEM_PCK.get_texto(281690) || qt_material_transf_w;
		end if;

		end;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_qt_maior_padrao_local ( cd_material_p bigint, qt_atendida_p bigint, nr_requisicao_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

