CREATE OR REPLACE PROCEDURE consiste_qt_max_mat_transf_etq (cd_material_p bigint, cd_local_estoque_p bigint, qt_material_p bigint, cd_unidade_medida_p text, qt_max_p INOUT bigint, qt_estoque_p INOUT bigint, qt_pendente_p INOUT bigint, qt_solicitacao_p INOUT bigint, cd_unidade_medida_estoque_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_unidade_medida_estoque_w	varchar(30)  := null;
cd_estabelecimento_w		smallint;
qt_existe_regra_w			smallint;
qt_max_w			double precision  := null;
qt_estoque_w			double precision  := null;
qt_pendente_w			double precision  := null;
qt_solicitacao_w			double precision  := null;
qt_total_w			double precision  := null;
ds_erro_w			varchar(4000) := null;


BEGIN
select	count(*)
into STRICT	qt_existe_regra_w
from	padrao_estoque_local
where	cd_local_estoque = cd_local_estoque_p
and	cd_material = cd_material_p;

if (qt_existe_regra_w > 0) then
	begin
	ds_erro_w := null;
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	local_estoque
	where	cd_local_estoque = cd_local_estoque_p;

	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UME'),1,30) cd_unidade_medida_estoque
	into STRICT	cd_unidade_medida_estoque_w
	from	material
	where	cd_material = cd_material_p;

	qt_max_w 		:= coalesce(obter_regra_padrao_local(cd_material_p, cd_local_estoque_p, cd_estabelecimento_w,'qt_max'),0);
	qt_estoque_w 	:= coalesce(obter_saldo_disp_estoque(cd_estabelecimento_w,cd_material_p,cd_local_estoque_p,trunc(clock_timestamp(),'month')),0);
	qt_pendente_w	:= coalesce(obter_qt_pendente_transf_etq(null, cd_material_p, cd_local_estoque_p),0);
	qt_solicitacao_w := coalesce(obter_quantidade_convertida(cd_material_p, qt_material_p, cd_unidade_medida_p, 'UME'),0);
	qt_total_w	:= (qt_estoque_w + qt_pendente_w + qt_solicitacao_w);

	if	(qt_max_w < (qt_estoque_w + qt_pendente_w + qt_solicitacao_w)) then
		ds_erro_w 			:= 'S';
	end if;
	end;
end if;
ds_erro_p			:= ds_erro_w;
qt_max_p			:= qt_max_w;
qt_estoque_p 		:= qt_estoque_w;
qt_pendente_p 		:= qt_pendente_w;
qt_solicitacao_p 	:= qt_solicitacao_w;
cd_unidade_medida_estoque_p := cd_unidade_medida_estoque_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
