SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_recalc_classif_tit_cp (nr_titulo_p bigint, nr_seq_baixa_p bigint, nm_usuario_p text, ds_lista_titulo_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w	bigint;
nr_seq_baixa_w		integer;
nr_titulo_contab_w	bigint;
ds_lista_titulo_w	varchar(4000);
nr_lote_contabilizado_w	titulo_pagar_baixa.nr_lote_contabil%type;

C01 CURSOR FOR
SELECT	nr_sequencia
from	titulo_pagar_baixa
where	nr_titulo = nr_titulo_p
and (coalesce(nr_seq_baixa_p,0) = 0 or nr_sequencia = nr_seq_baixa_p);



BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

open C01;
loop
fetch C01 into
	nr_seq_baixa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_seq_baixa_p IS NOT NULL AND nr_seq_baixa_p::text <> '') then

			nr_lote_contabilizado_w	:= null;

			select	max(a.nr_lote_contabil)
			into STRICT	nr_lote_contabilizado_w
			from	titulo_pagar_baixa a
			where	a.nr_titulo	= nr_titulo_p
			and	a.nr_sequencia	= nr_seq_baixa_w
			and	a.nr_lote_contabil <> 0;

			if (nr_lote_contabilizado_w IS NOT NULL AND nr_lote_contabilizado_w::text <> '') then
				--' TÃ­tulo: ' || nr_titulo_p || ' - Baixa: ' || nr_seq_baixa_w;
				ds_lista_titulo_w := substr(ds_lista_titulo_w || wheb_mensagem_pck.get_texto(301887,'NR_TITULO_P=' || nr_titulo_p || ';NR_SEQ_BAIXA_W=' || nr_seq_baixa_w),1,255);
			end if;
	end if;

	end;
end loop;
close C01;

if (ds_lista_titulo_w IS NOT NULL AND ds_lista_titulo_w::text <> '') then
	ds_lista_titulo_p := substr(ds_lista_titulo_w,1,255);
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
