SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_receb_moeda_estrang ( nr_seq_caixa_rec_p bigint) AS $body$
DECLARE


cd_moeda_w		integer;
qt_cheque_moeda_w	bigint := 0;
qt_especie_moeda_w	bigint := 0;
qt_titulo_moeda_w	bigint := 0;
qt_adiant_moeda_w	bigint := 0;
qt_movto_moeda_w	bigint := 0;
qt_adiant_doc_mon_w	bigint := 0;
qt_monetario_w		bigint := 0;
qt_documento_w		bigint := 0;
qt_troca_moeda_w	bigint := 0;

c01 CURSOR FOR
	SELECT	distinct cd_moeda
	from	caixa_receb_estrang
	where	nr_seq_caixa_receb	= nr_seq_caixa_rec_p
	and	(cd_moeda IS NOT NULL AND cd_moeda::text <> '')
	
union

	/* Títulos */

	SELECT	distinct cd_moeda
	from	titulo_receber_liq
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	(cd_moeda IS NOT NULL AND cd_moeda::text <> '')
	
union

	/* Adiantamento */

	select	distinct cd_moeda
	from	adiantamento
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	(cd_moeda IS NOT NULL AND cd_moeda::text <> '')
	
union

	/* Cheque */

	select	distinct cd_moeda
	from	cheque_cr
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	(cd_moeda IS NOT NULL AND cd_moeda::text <> '')
	
union

	/* Outros documentos */

	select	distinct a.cd_moeda
	from	movto_trans_financ a,
		transacao_financeira b
	where	a.nr_seq_trans_financ = b.nr_sequencia
	and	b.ie_caixa = 'D'
	and	coalesce(a.nr_seq_titulo_receber::text, '') = ''
	and	coalesce(a.nr_adiantamento::text, '') = ''
	and	a.nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and 	(a.cd_moeda IS NOT NULL AND a.cd_moeda::text <> '')
	
union

	/* Troca de Valores */

	select	distinct cd_moeda
	from	adiantamento_pago
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	(cd_moeda IS NOT NULL AND cd_moeda::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	cd_moeda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	-- Verifica se tem recebimento em espécie em moeda estrangeira
	select	count(*)
	into STRICT	qt_especie_moeda_w
	from	caixa_receb_estrang
	where	nr_seq_caixa_receb = nr_seq_caixa_rec_p
	and	cd_moeda = cd_moeda_w
	and	coalesce(vl_especie_estrang,0) <> 0;

	-- Verifica se tem cheque em moeda estrangeira
	select	count(*)
	into STRICT	qt_cheque_moeda_w
	from	cheque_cr
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	cd_moeda = cd_moeda_w
	and	coalesce(vl_cheque_estrang,0) <> 0;

	-- Verifica se tem baixa de título em moeda estrangeira
	select	count(*)
	into STRICT	qt_titulo_moeda_w
	from	titulo_receber_liq
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	cd_moeda = cd_moeda_w
	and	coalesce(vl_recebido_estrang,0) <> 0;

	-- Verifica se tem adiantamento em moeda estrangeira
	select	count(*)
	into STRICT	qt_adiant_moeda_w
	from	adiantamento
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	cd_moeda = cd_moeda_w
	and	coalesce(vl_adto_estrang,0) <> 0;

	-- Verifica se tem outros documentos em moeda estrangeira
	select	count(*)
	into STRICT	qt_movto_moeda_w
	from	movto_trans_financ a,
		transacao_financeira b
	where	a.nr_seq_trans_financ = b.nr_sequencia
	and	b.ie_caixa = 'D'
	and	coalesce(a.nr_seq_titulo_receber::text, '') = ''
	and	coalesce(a.nr_adiantamento::text, '') = ''
	and	a.nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	a.cd_moeda = cd_moeda_w
	and	coalesce(a.vl_transacao_estrang,0) <> 0;

	-- Verifica se tem troca de valores em moeda estrangeira
	select	count(*)
	into STRICT	qt_troca_moeda_w
	from	adiantamento_pago
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	and	cd_moeda = cd_moeda_w
	and	coalesce(vl_adto_estrang,0) <> 0;

	-- Verifica se o adiantamento lançado possui transação monetária + documento
	if (qt_adiant_moeda_w > 0) then
		select	count(*)
		into STRICT	qt_adiant_doc_mon_w
		from	adiantamento a,
			transacao_financeira b
		where	a.nr_seq_trans_caixa = b.nr_sequencia
		and	a.nr_seq_caixa_rec = nr_seq_caixa_rec_p
		and	a.cd_moeda = cd_moeda_w
		and	coalesce(a.vl_adto_estrang,0) <> 0
		and	b.ie_caixa = 'A';
	end if;

	-- Soma a quantidade de registros monetários
	qt_monetario_w := qt_especie_moeda_w + qt_cheque_moeda_w + qt_adiant_doc_mon_w;
	-- Soma a quantidade de registros de documento
	qt_documento_w := qt_titulo_moeda_w + qt_adiant_moeda_w + qt_movto_moeda_w + qt_troca_moeda_w;

	if (qt_monetario_w > 0) then
		if (qt_documento_w = 0) then
			/* Existem recebimentos monetários em #@DS_MOEDA#@ sem recebimento de documento na mesma moeda. */

			CALL wheb_mensagem_pck.exibir_mensagem_abort(352851,'DS_MOEDA='||obter_ds_moeda_estrangeira(cd_moeda_w));
		end if;
	end if;
	if (qt_documento_w > 0) then
		if (qt_monetario_w = 0) then
			/* Existem recebimentos de documentos em #@DS_MOEDA#@ sem recebimento monetário na mesma moeda. */

			CALL wheb_mensagem_pck.exibir_mensagem_abort(352854,'DS_MOEDA='||obter_ds_moeda_estrangeira(cd_moeda_w));
		end if;
	end if;

	qt_monetario_w := 0;
	qt_documento_w := 0;
	qt_especie_moeda_w := 0;
	qt_cheque_moeda_w := 0;
	qt_adiant_moeda_w := 0;
	qt_titulo_moeda_w := 0;
	qt_movto_moeda_w := 0;
	qt_adiant_doc_mon_w := 0;
	qt_troca_moeda_w := 0;

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_receb_moeda_estrang ( nr_seq_caixa_rec_p bigint) FROM PUBLIC;

