SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_regra_agecons_conv ( cd_convenio_p bigint, cd_categoria_p text, cd_agenda_p bigint, cd_setor_atendimento_p bigint, cd_plano_convenio_p text, cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_estabelecimento_p bigint, cd_empresa_ref_p bigint, nr_sequencia_p bigint default null) AS $body$
DECLARE


nr_seq_regra_w					bigint;
ie_agenda_w						varchar(1);
ie_excecao_paciente_w 			bigint;
ie_permite_paciente_w			varchar(1);
ds_mensagem_w					varchar(255);
cd_setor_exclusivo_w			integer;
cd_especialidade_w				integer;
cd_medico_agenda_w				varchar(10);
qt_agendamentos_w				bigint;
ie_agendado_w					varchar(1);
ie_dia_semana_w					smallint;
nr_seq_plano_w					bigint;
qt_agen_mesmo_dia_w				bigint;
ie_bloq_mesma_espec_dia_w 		varchar(1);
cd_municipio_ibge_w				varchar(6);
ie_paciente_mais_restritivo_w	varchar(1);

c01 CURSOR FOR
	SELECT 	coalesce(nr_sequencia,0)
	from	regra_agecons_convenio
	where	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''))
	and		((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and		((cd_agenda = cd_agenda_p) or (coalesce(cd_agenda::text, '') = ''))
	and		((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and		((cd_plano_convenio = cd_plano_convenio_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and		((((cd_setor_atendimento = cd_setor_atendimento_p) and coalesce(ie_forma_consiste_setor,'A') = 'A')
			or	((cd_setor_atendimento = cd_setor_exclusivo_w) and coalesce(ie_forma_consiste_setor,'E') = 'E'))
			or (coalesce(cd_setor_atendimento::text, '') = ''))
	and		((cd_especialidade = cd_especialidade_w OR cd_especialidade IN (SELECT cd_especialidade FROM agenda_cons_especialidade WHERE cd_agenda = cd_agenda_p)) or (coalesce(cd_especialidade::text, '') = ''))
	and		((cd_medico = cd_medico_agenda_w) or (coalesce(cd_medico::text, '') = ''))
	and		((nr_seq_plano = nr_seq_plano_w) or (coalesce(nr_seq_plano::text, '') = ''))
	and		((coalesce(ie_primeiro_agendamento,'N') = 'S' and ie_agendado_w = 'N') or (coalesce(ie_primeiro_agendamento,'N') = 'N'))
	and		((cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''))
	and (trunc(dt_agenda_p) >= dt_inicial_vigencia or coalesce(dt_inicial_vigencia::text, '') = '')
	and (trunc(dt_agenda_p) <= dt_final_vigencia or coalesce(dt_final_vigencia::text, '') = '')
	and		((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''))
	and (dt_agenda_p between to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_inicial,trunc(dt_agenda_p,'dd')),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and 	to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_final,trunc(dt_agenda_p)+86399/86400),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'))
	and 	((cd_empresa_ref = cd_empresa_ref_p) or (coalesce(cd_empresa_ref::text, '') = ''))
	and 	((cd_municipio_ibge = cd_municipio_ibge_w) or (coalesce(cd_municipio_ibge::text, '') = ''))
	order 	by
			coalesce(cd_convenio,0),
			coalesce(cd_pessoa_fisica,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(cd_plano_convenio,0),
			coalesce(cd_categoria,0),
			coalesce(cd_agenda,0),
			coalesce(nr_seq_plano,0),
			coalesce(cd_medico,0),
			coalesce(cd_especialidade,0),
			coalesce(dt_inicial_vigencia,clock_timestamp()),
			coalesce(dt_final_vigencia,clock_timestamp()),
			coalesce(hr_inicial,clock_timestamp()),
			coalesce(hr_final,clock_timestamp()),
			coalesce(cd_municipio_ibge,0);


c02 CURSOR FOR
	SELECT 	coalesce(ie_permite,'S')
	into STRICT	ie_permite_paciente_w
	from	regra_agecons_convenio
	where	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''))
	and		((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and		((cd_agenda = cd_agenda_p) or (coalesce(cd_agenda::text, '') = ''))
	and		((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and		((cd_plano_convenio = cd_plano_convenio_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and		((((cd_setor_atendimento = cd_setor_atendimento_p) and coalesce(ie_forma_consiste_setor,'A') = 'A')
			or	((cd_setor_atendimento = cd_setor_exclusivo_w) and coalesce(ie_forma_consiste_setor,'E') = 'E'))
			or (coalesce(cd_setor_atendimento::text, '') = ''))
	and		((cd_especialidade = cd_especialidade_w OR cd_especialidade IN (SELECT cd_especialidade FROM agenda_cons_especialidade WHERE cd_agenda = cd_agenda_p)) or (coalesce(cd_especialidade::text, '') = ''))
	and		((cd_medico = cd_medico_agenda_w) or (coalesce(cd_medico::text, '') = ''))
	and		((nr_seq_plano = nr_seq_plano_w) or (coalesce(nr_seq_plano::text, '') = ''))
	and (trunc(dt_agenda_p) >= dt_inicial_vigencia or coalesce(dt_inicial_vigencia::text, '') = '')
	and (trunc(dt_agenda_p) <= dt_final_vigencia or coalesce(dt_final_vigencia::text, '') = '')
	and		((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''))
	and (dt_agenda_p between to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_inicial,trunc(dt_agenda_p,'dd')),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	and 	to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_final,trunc(dt_agenda_p)+86399/86400),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'))
	and		cd_pessoa_fisica = cd_pessoa_fisica_p
	order 	by
			coalesce(cd_pessoa_fisica,0),
			coalesce(cd_convenio,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(cd_plano_convenio,0),
			coalesce(cd_categoria,0),
			coalesce(cd_agenda,0),
			coalesce(nr_seq_plano,0),
			coalesce(cd_medico,0),	
			coalesce(cd_especialidade,0),			
			coalesce(dt_inicial_vigencia,clock_timestamp()),
			coalesce(dt_final_vigencia,clock_timestamp()),
			coalesce(hr_inicial,clock_timestamp()),
			coalesce(hr_final,clock_timestamp()),
			coalesce(cd_municipio_ibge,0);


BEGIN
	ie_paciente_mais_restritivo_w := obter_param_usuario(821, 490, obter_perfil_ativo, obter_usuario_ativo, cd_estabelecimento_p, ie_paciente_mais_restritivo_w);

	ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_p);

	select	max(cd_municipio_ibge)
	into STRICT	cd_municipio_ibge_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		ie_tipo_complemento	= 1;

	select	max(cd_setor_exclusivo),
			max(cd_pessoa_fisica),
			max(cd_especialidade)
	into STRICT	cd_setor_exclusivo_w,
			cd_medico_agenda_w,
			cd_especialidade_w
	from	agenda
	where	cd_agenda = cd_agenda_p;

	select	max(a.nr_seq_plano)
	into STRICT	nr_seq_plano_w
	from	pls_segurado	a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > clock_timestamp()))
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

	select	count(*)
	into STRICT	qt_agendamentos_w
	from	agenda_consulta a,
			agenda b
	where	a.cd_agenda 		= b.cd_agenda
	and		b.cd_pessoa_fisica 	= cd_medico_agenda_w
	and		a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and (a.cd_convenio		= cd_convenio_p or (coalesce(cd_convenio_p::text, '') = '' and coalesce(a.cd_convenio::text, '') = ''))
	and		a.ie_status_agenda not in ('C','F');

	if (qt_agendamentos_w > 0) then
		ie_agendado_w := 'S';
	else
		ie_agendado_w := 'N';
	end if;

	open c01;
	loop
	fetch c01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_seq_regra_w := nr_seq_regra_w;
		end;
	end loop;
	close c01;

	if (nr_seq_regra_w > 0) then

		select	coalesce(max(ie_permite),'S'),
				coalesce(max(ds_mensagem),'')
		into STRICT	ie_agenda_w,
				ds_mensagem_w
		from	regra_agecons_convenio
		where	nr_sequencia = nr_seq_regra_w
		and		((cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''))
		and		coalesce(ie_bloq_mesma_espec,'N') = 'S';

		if (ie_agenda_w = 'N') then
			-- VERIFICA SE O PACIENTE JA POSSUI AGENDAMENTO PARA A MESMA ESPECIALIDADE NO MESMO DIA
			select	count(*)
			into STRICT	qt_agen_mesmo_dia_w
			from	agenda_consulta a,
					agenda b
			where	a.cd_agenda 		= b.cd_agenda
			and		b.cd_tipo_agenda	= 3
			and		a.cd_pessoa_fisica	= cd_pessoa_fisica_p
			and (a.cd_convenio		= CASE WHEN cd_convenio_p=0 THEN null  ELSE cd_convenio_p END  or (CASE WHEN cd_convenio_p=0 THEN null  ELSE cd_convenio_p coalesce(END::text, '') = '' and coalesce(a.cd_convenio::text, '') = ''))
			and		a.ie_status_agenda 	not in ('C','F')
			and (b.cd_especialidade	= obter_especialidade_agenda(cd_agenda_p) OR b.cd_especialidade IN (SELECT cd_especialidade FROM agenda_cons_especialidade WHERE cd_agenda = cd_agenda_p))
			and		trunc(a.dt_agenda) 	= trunc(dt_agenda_p)
			and 	a.nr_sequencia		<> coalesce(nr_sequencia_p,0);

			if (qt_agen_mesmo_dia_w > 0) then
				if (coalesce(ds_mensagem_w::text, '') = '') then
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262537);
				else
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262328 , 'DS_MENSAGEM='||ds_mensagem_w);
				end if;
			end if;
		end if;

		select	coalesce(max(ie_permite),'S'),
				coalesce(max(ds_mensagem),'')
		into STRICT	ie_agenda_w,
				ds_mensagem_w
		from	regra_agecons_convenio
		where	nr_sequencia = nr_seq_regra_w
		and		((cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''))
		and		coalesce(ie_bloq_mesma_espec,'N') = 'N';


		if (coalesce(ie_paciente_mais_restritivo_w,'N') = 'N') then
			select 	count(*)
			into STRICT	ie_excecao_paciente_w
			from	regra_agecons_convenio
			where	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''))
			and		((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
			and		((cd_agenda = cd_agenda_p) or (coalesce(cd_agenda::text, '') = ''))
			and		((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
			and		((cd_plano_convenio = cd_plano_convenio_p) or (coalesce(cd_plano_convenio::text, '') = ''))
			and		((((cd_setor_atendimento = cd_setor_atendimento_p) and coalesce(ie_forma_consiste_setor,'A') = 'A')
					or	((cd_setor_atendimento = cd_setor_exclusivo_w) and coalesce(ie_forma_consiste_setor,'E') = 'E'))
					or (coalesce(cd_setor_atendimento::text, '') = ''))
			and		((cd_especialidade = cd_especialidade_w OR cd_especialidade IN (SELECT cd_especialidade FROM agenda_cons_especialidade WHERE cd_agenda = cd_agenda_p)) or (coalesce(cd_especialidade::text, '') = ''))
			and		((cd_medico = cd_medico_agenda_w) or (coalesce(cd_medico::text, '') = ''))
			and		((nr_seq_plano = nr_seq_plano_w) or (coalesce(nr_seq_plano::text, '') = ''))
			and (trunc(dt_agenda_p) >= dt_inicial_vigencia or coalesce(dt_inicial_vigencia::text, '') = '')
			and (trunc(dt_agenda_p) <= dt_final_vigencia or coalesce(dt_final_vigencia::text, '') = '')
			and		((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''))
			and (dt_agenda_p between to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_inicial,trunc(dt_agenda_p,'dd')),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
			and 	to_date(to_char(dt_agenda_p,'dd/mm/yyyy') ||' '|| to_char(coalesce(hr_final,trunc(dt_agenda_p)+86399/86400),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'))
			and		cd_pessoa_fisica = cd_pessoa_fisica_p
			and		ie_permite = 'S';

			if	(ie_agenda_w = 'N' AND ie_excecao_paciente_w = 0) then
				if (coalesce(ds_mensagem_w::text, '') = '') then
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262537);
				else
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262328 , 'DS_MENSAGEM='||ds_mensagem_w);
				end if;
			end if;
		else
			ie_permite_paciente_w := ie_agenda_w;
			open c02;
			loop
			fetch c02 into
				ie_permite_paciente_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				begin
				ie_permite_paciente_w := ie_permite_paciente_w;
				end;
			end loop;
			close c02;

			if (ie_permite_paciente_w = 'N') then
				if (coalesce(ds_mensagem_w::text, '') = '') then
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262537);
				else
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(262328 , 'DS_MENSAGEM='||ds_mensagem_w);
				end if;
			end if;

		end if;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_regra_agecons_conv ( cd_convenio_p bigint, cd_categoria_p text, cd_agenda_p bigint, cd_setor_atendimento_p bigint, cd_plano_convenio_p text, cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_estabelecimento_p bigint, cd_empresa_ref_p bigint, nr_sequencia_p bigint default null) FROM PUBLIC;

