CREATE OR REPLACE PROCEDURE consiste_regra_ajuste_proc ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_procedimento_p timestamp, cd_setor_atendimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
 
