	coalesce(max(obter_dados_categ_conv(nr_atendimento_p, 'COB')),0), 
	max(obter_dados_categ_conv(nr_atendimento_p, 'DEP')) 
into STRICT	cd_convenio_w, 
	ie_tipo_atendimento_w, 
	cd_plano_convenio_w, 
	cd_categoria_w, 
	cd_tipo_acomod_w, 
	nr_seq_cobertura_w, 
	cd_dependente_w
;
 
select	coalesce(max(nr_seq_classificacao),0), 
	max(cd_medico_resp), 
	max(ie_carater_inter_sus), 
	coalesce((max(obter_dados_categ_conv(nr_atendimento,'OC')))::numeric ,0), 
	coalesce(max(nr_seq_classif_medico),0) 
into STRICT	nr_seq_classif_atend_w, 
