SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_regra_conv_pessoa ( cd_convenio_p bigint, ie_tipo_atendimento_p bigint, ie_clinica_p bigint, ie_carater_inter_sus_p text, cd_pessoa_fisica_p text, ds_erro_p INOUT text, ie_bloqueia_p INOUT text, ds_campos_p INOUT text, nr_seq_regra_compl_pf_p INOUT bigint) AS $body$
DECLARE


qt_regras_w			bigint;
quebra_w				varchar(10)	:= chr(13)||chr(10);

cd_cartao_nac_sus_w		varchar(20);
nr_seq_cor_pele_w			bigint;
nr_cep_cidade_nasc_w		varchar(15);

ie_consiste_cns_w			varchar(1);
ie_consiste_raca_cor_w		varchar(1);
ie_consiste_cidade_natal_w		varchar(1);

ie_consiste_cep_w			varchar(1);
ie_consiste_end_w			varchar(1);
ie_consiste_num_end_w		varchar(1);
ie_consiste_mun_ibge_w		varchar(1);
ie_consiste_cpf_w       		varchar(1);
ie_consiste_rg_w         		varchar(1);

cd_cep_w			varchar(15);
ds_endereco_w			varchar(100);
nr_endereco_w			bigint;
cd_municipio_ibge_w		varchar(6);
cd_perfil_w			integer := obter_perfil_ativo;
cd_estabelecimento_w		smallint := obter_estabelecimento_ativo;
cd_setor_atendimento_w		integer	:= obter_setor_ativo;
nr_cpf_w				varchar(11);
nr_identidade_w			varchar(15);
ds_mensagem_w			varchar(255);
nr_seq_regra_compl_pf_w		bigint;

C01 CURSOR FOR
	SELECT	a.ie_consiste_cns,
		a.ie_consiste_cidade_natal,
		a.ie_consiste_raca,
		a.ie_consiste_cep,
		a.ie_consiste_endereco,
		a.ie_consiste_numero,
		a.ie_consiste_municipio_ibge,
		a.ie_consiste_cpf,
		a.ie_consiste_rg,
		coalesce(a.nr_seq_regra_compl_pf,0)
	from	regra_cons_convenio_pessoa a
	where	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0)
	and	coalesce(a.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and	coalesce(a.ie_clinica,coalesce(ie_clinica_p,0))			= coalesce(ie_clinica_p,0)
	and	coalesce(a.ie_carater_inter_sus,coalesce(ie_carater_inter_sus_p,'0'))	= coalesce(ie_carater_inter_sus_p,'0')
	and	coalesce(a.cd_perfil,cd_perfil_w)				= cd_perfil_w
	and	coalesce(a.cd_estabelecimento,cd_estabelecimento_w)		= cd_estabelecimento_w
	and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0)
	order by coalesce(a.cd_convenio,0);


BEGIN
ds_erro_p 	:= '';
ie_bloqueia_p	:= 'N';

/*Verifica a existência de regras*/

select	count(*)
into STRICT	qt_regras_w
from	regra_cons_convenio_pessoa a
where	coalesce(a.cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0)
and	coalesce(a.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
and	coalesce(a.ie_clinica,coalesce(ie_clinica_p,0))			= coalesce(ie_clinica_p,0)
and	coalesce(a.ie_carater_inter_sus,coalesce(ie_carater_inter_sus_p,'0'))	= coalesce(ie_carater_inter_sus_p,'0')
and	coalesce(a.cd_perfil,cd_perfil_w) 				= cd_perfil_w
and	coalesce(a.cd_estabelecimento,cd_estabelecimento_w)		= cd_estabelecimento_w
and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0);


if (qt_regras_w > 0) then

		/*Busca a informação das regras*/

		open C01;
		loop
		fetch C01 into
			ie_consiste_cns_w,
			ie_consiste_cidade_natal_w,
			ie_consiste_raca_cor_w,
			ie_consiste_cep_w,
			ie_consiste_end_w,
			ie_consiste_num_end_w,
			ie_consiste_mun_ibge_w,
			ie_consiste_cpf_w,
			ie_consiste_rg_w,
			nr_seq_regra_compl_pf_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ie_consiste_cns_w := ie_consiste_cns_w;
			end;
		end loop;
		close C01;



		select	a.nr_cartao_nac_sus,
			a.nr_seq_cor_pele,
			a.nr_cep_cidade_nasc,
			a.nr_cpf,
			a.nr_identidade
		into STRICT	cd_cartao_nac_sus_w,
			nr_seq_cor_pele_w,
			nr_cep_cidade_nasc_w,
			nr_cpf_w,
			nr_identidade_w
		from	pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	max(a.cd_cep),
			max(a.ds_endereco),
			coalesce(max(a.nr_endereco),0),
			max(a.cd_municipio_ibge)
		into STRICT	cd_cep_w,
			ds_endereco_w,
			nr_endereco_w,
			cd_municipio_ibge_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.ie_tipo_complemento = 1;


		if (ie_consiste_cns_w = 'S') and (coalesce(cd_cartao_nac_sus_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281831,null);
		elsif (ie_consiste_cns_w = 'B') and (coalesce(cd_cartao_nac_sus_w::text, '') = '') then
			ds_erro_p	:= ds_erro_p	||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281831,null);
			ie_bloqueia_p	:= 'S';
			ds_campos_p	:= ds_campos_p||'NR_CARTAO_NAC_SUS'||', ';
		end if;


		if (ie_consiste_raca_cor_w = 'S') and (coalesce(nr_seq_cor_pele_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281832,null);
		elsif (ie_consiste_raca_cor_w = 'B') and (coalesce(nr_seq_cor_pele_w::text, '') = '') then
			ds_erro_p	:= ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281832,null);
			ie_bloqueia_p	:= 'S';
			ds_campos_p	:= ds_campos_p||'NR_SEQ_COR_PELE'||',  ';
		end if;


		if (ie_consiste_cidade_natal_w = 'S') and (coalesce(nr_cep_cidade_nasc_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281833,null);
		elsif (ie_consiste_cidade_natal_w = 'B') and (coalesce(replace(nr_cep_cidade_nasc_w,' ','')::text, '') = '') then
			ds_erro_p	:= ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281833,null);
			ie_bloqueia_p	:= 'S';
			ds_campos_p	:= ds_campos_p||'NR_CEP_CIDADE_NASC'||',';
		end if;


		if (ie_consiste_cep_w = 'S') and (coalesce(cd_cep_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281834,null);
		end if;

		if (ie_consiste_end_w = 'S') and (coalesce(ds_endereco_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281835,null);
		end if;

		if (ie_consiste_num_end_w = 'S') and (nr_endereco_w = 0) then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281836,null);
		end if;

		if (ie_consiste_mun_ibge_w = 'S') and (coalesce(cd_municipio_ibge_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281837,null);
		end if;

		if (ie_consiste_cpf_w = 'S') and (coalesce(nr_cpf_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281838,null);
		elsif (ie_consiste_cpf_w = 'B') and (coalesce(nr_cpf_w::text, '') = '') then
			ds_erro_p	:= ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281838,null);
			ie_bloqueia_p	:= 'S';
			ds_campos_p	:= ds_campos_p||'NR_CPF'||',  ';
		end if;

		if (ie_consiste_rg_w = 'S') and (coalesce(nr_identidade_w::text, '') = '') then
			ds_erro_p := ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281839,null);
		elsif (ie_consiste_rg_w = 'B') and (coalesce(nr_identidade_w::text, '') = '') then
			ds_erro_p	:= ds_erro_p||quebra_w||WHEB_MENSAGEM_PCK.get_texto(281839,null);
			ie_bloqueia_p	:= 'S';
			ds_campos_p	:= ds_campos_p||'NR_IDENTIDADE'||',  ';
		end if;

		if (coalesce(nr_seq_regra_compl_pf_w,0) > 0) then

			nr_seq_regra_compl_pf_p := nr_seq_regra_compl_pf_w;
			SELECT * FROM consis_atrib_cad_compl_pf_eup(cd_pessoa_fisica_p, nr_seq_regra_compl_pf_w, ds_mensagem_w, ie_bloqueia_p) INTO STRICT ds_mensagem_w, ie_bloqueia_p;
			if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
			ds_erro_p := ds_erro_p || quebra_w ||ds_mensagem_w;
			end if;

		end if;
end if;

ds_erro_p := replace(substr(ds_erro_p,2,254),',',quebra_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_regra_conv_pessoa ( cd_convenio_p bigint, ie_tipo_atendimento_p bigint, ie_clinica_p bigint, ie_carater_inter_sus_p text, cd_pessoa_fisica_p text, ds_erro_p INOUT text, ie_bloqueia_p INOUT text, ds_campos_p INOUT text, nr_seq_regra_compl_pf_p INOUT bigint) FROM PUBLIC;

