SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_regra_mat_quimio ( cd_material_p bigint) AS $body$
DECLARE



cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ie_permite_w			varchar(10);

c01 CURSOR FOR
	SELECT	IE_PERMITE_PRESCR
	from	REGRA_PRESCR_MAT_QUIMIO
	where	coalesce(cd_material, cd_material_p)				= cd_material_p
	and	coalesce(CD_GRUPO_MAT, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(CD_SUBGRUPO_MAT, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_mat, cd_classe_material_w)		= cd_classe_material_w
	order	by  	coalesce(cd_material,0),
			coalesce(cd_classe_mat,0),
			coalesce(cd_subgrupo_mat,0),
			coalesce(CD_GRUPO_MAT,0);


BEGIN

ie_permite_w	:= 'S';

begin
select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material	= cd_material_p;
exception
	when others then
		begin
		cd_grupo_material_w:= '';
		cd_subgrupo_material_w:= '';
		cd_classe_material_w:= '';
		end;
end;


open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (ie_permite_w	= 'N') then
	 -- Não é possível prescrever o Material / Medicamento '||obter_desc_material(cd_material_p)||'. #@#@
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(263532,	'DS_MATERIAL_W='||substr(obter_desc_material(cd_material_p),1,60));
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
