SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_regra_protocolo ( cd_convenio_p bigint, ie_tipo_protocolo_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= '';
qt_reg_w	integer	:= 0;
qt_reg_regra_w	integer	:= 0;


BEGIN

/* Verificar se existe regra para o convênio e tipo do protocolo */

select	count(*)
into STRICT	qt_reg_w
from	regra_prot_setor_conv
where	cd_convenio		= cd_convenio_p
and	ie_tipo_protocolo	= ie_tipo_protocolo_p
and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0);

/* Se existir regra, verificar se o setor de atendimento do usuário pode acessar os protocolos*/

if (qt_reg_w > 0 ) then

        select	count(*)
        into STRICT	qt_reg_regra_w
        from	regra_prot_setor_conv
        where	cd_convenio		= cd_convenio_p
        and	ie_tipo_protocolo 	= ie_tipo_protocolo_p
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
        and	cd_setor_atendimento 	= cd_setor_atendimento_p;

        /*Se o setor do usuário não está na regra então não poderá visualizar o protocolo*/

        if (qt_reg_regra_w	= 0) then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(313056);
        end if;
end if;

ds_retorno_p	:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
