CREATE OR REPLACE PROCEDURE consiste_reg_incons_farm_soluc ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_possui_lote_w	varchar(1);
ds_erro_w		varchar(255)	:=	null;
ie_consiste_item_w	varchar(1);


BEGIN

if (obter_funcao_ativa = 252) then
	ie_consiste_item_w := Obter_Param_Usuario(252, 46, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_item_w);
else
	ie_consiste_item_w := Obter_Param_Usuario(7010, 111, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_item_w);
end if;

if (ie_consiste_item_w = 'S') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_possui_lote_w
	from	prescr_mat_hor
	where	nr_prescricao 	= nr_prescricao_p
	and	nr_seq_solucao = nr_sequencia_p
	and	(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '');

	if (ie_possui_lote_w = 'S') then
		ds_erro_w := 	substr(wheb_mensagem_pck.get_texto(278181),1,255);
	end if;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
