CREATE OR REPLACE PROCEDURE consiste_responsavel_ama ( cd_responsavel_p text, nm_usuario_p text, nr_atendimento_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
ie_ama_w				varchar(1);
ie_ama_ww				varchar(1) := 'N';
cd_setor_atendimento_w	integer;

 
C01 CURSOR FOR 
	SELECT	coalesce(ie_ama,'N') ie_ama 
	from	sus_medico_ama 
	where	coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w 
	and 	coalesce(cd_pessoa_fisica,cd_responsavel_p) = cd_responsavel_p 
	order by coalesce(ie_ama,'N');
	

BEGIN 
 
ds_retorno_p	:= '';
cd_setor_atendimento_w	:= Obter_Dados_Atendimento(nr_atendimento_p, 'PSCD');
 
 
select	coalesce(max(cd_pessoa_fisica),'0'), 
	coalesce(max(ie_ama),'S') 
into STRICT	cd_pessoa_fisica_w, 
	ie_ama_w 
from	medico 
where	cd_pessoa_fisica	= cd_responsavel_p;
 
open C01;
	loop 
	fetch C01 into 
		ie_ama_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */	
		ie_ama_ww := ie_ama_ww;
	end loop;
	close C01;
 
if (cd_pessoa_fisica_w <> '0') and (ie_ama_w <> 'S') and (ie_ama_ww <> 'S') then 
	ds_retorno_p	:= Wheb_mensagem_pck.get_texto(799520);
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
