CREATE OR REPLACE PROCEDURE consiste_restricao_mat_setor ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_permite_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_erro_restricao_w	varchar(1);


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (ie_permite_p IS NOT NULL AND ie_permite_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_erro_restricao_w
	from	material_setor_exclusivo
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_material		= cd_material_p
	and	ie_permite		<> ie_permite_p
	and	nr_sequencia		= nr_sequencia_p;

	if (ie_erro_restricao_w = 'S') then
		begin
		-- Todos os setores cadastrados devem ter o mesmo tipo de permissão ou restrição.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(140859);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
