SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_rx_conta ( nr_interno_conta_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w		varchar(255);
dt_procedimento_w	timestamp;
tx_procedimento_w	double precision;
cd_tipo_proced_w	smallint;

C01 CURSOR FOR
SELECT b.cd_tipo_procedimento,
	trunc(a.dt_procedimento,'dd'),
	tx_procedimento
from	procedimento b,
	procedimento_paciente a
where	b.cd_tipo_procedimento in (1,97,104)
and	a.cd_procedimento	= b.cd_procedimento
and	a.ie_origem_proced	= b.ie_origem_proced
and	a.ie_origem_proced	in (1,5)
and	a.tx_procedimento	= 100
and	a.nr_interno_conta	= nr_interno_conta_p
and 	coalesce(a.nr_seq_proc_pacote,0) <> a.nr_sequencia
group by
	b.cd_tipo_procedimento,
	trunc(a.dt_procedimento,'dd'),
	tx_procedimento
having count(*) > 1 and sum(a.qt_procedimento) > 0;


BEGIN

ds_erro_w	:= '';

OPEN C01;
LOOP
FETCH C01 into
		cd_tipo_proced_w,
		dt_procedimento_w,
		tx_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_erro_w	:= '54 ';
END LOOP;
CLOSE C01;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
