CREATE OR REPLACE PROCEDURE consiste_sequencia_apres_teste ( nr_seq_proj_gpi_p bigint, nr_sequencia_p bigint, nr_seq_superior_p bigint, nr_seq_apres_p bigint, ie_opcao_p text, ds_erro_p INOUT text) AS $body$
DECLARE


/*	IE_OPCAO_P
	C - Caso de testes
	I - Item caso de testes
	A - Ação caso de testes 		*/
nr_sequencia_w			bigint;
nr_seq_teste_w			bigint;
nr_seq_caso_teste_ant_w		bigint;
ds_teste_w			varchar(255);
ds_erro_w			varchar(255);


BEGIN

if (ie_opcao_p = 'C') then
	begin
	select	coalesce(max(nr_seq_caso_teste_ant),0)
	into STRICT	nr_seq_caso_teste_ant_w
	from	teste_caso_teste
	where	nr_sequencia = nr_sequencia_p;

	select	max(b.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	man_ordem_servico b,
		teste_caso_teste a
	where	b.nr_sequencia = a.nr_seq_ordem_servico
	and	coalesce(a.nr_seq_caso_teste_ant::text, '') = ''
	and	b.nr_seq_proj_gpi = nr_seq_proj_gpi_p
	and	a.nr_seq_apres = nr_seq_apres_p
	and	a.nr_sequencia <> nr_sequencia_p
	and	a.nr_sequencia <> nr_seq_caso_teste_ant_w;

	if (coalesce(nr_sequencia_w,0) > 0) then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281191) || nr_seq_apres_p || WHEB_MENSAGEM_PCK.get_texto(281192) || nr_sequencia_w || '".';
	else
		begin
		select	max(b.nr_sequencia),
			max(b.ds_teste)
		into STRICT	nr_sequencia_w,
			ds_teste_w
		from	teste_software b,
			teste_caso_teste a
		where	b.nr_sequencia = a.nr_seq_teste
		and	coalesce(a.nr_seq_caso_teste_ant::text, '') = ''
		and	b.nr_seq_proj_gpi = nr_seq_proj_gpi_p
		and	a.nr_seq_apres = nr_seq_apres_p
		and	a.nr_sequencia <> nr_sequencia_p;

		if (coalesce(nr_sequencia_w,0) > 0) then
			ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281191) || nr_seq_apres_p || WHEB_MENSAGEM_PCK.get_texto(281193) || ds_teste_w || '".';
		else
			begin
			select	max(b.nr_sequencia)
			into STRICT	nr_seq_teste_w
			from	teste_software b,
				teste_caso_teste a
			where	b.nr_sequencia = a.nr_seq_teste
			and	a.nr_sequencia = nr_sequencia_p;

			if (coalesce(nr_seq_teste_w,0) > 0) then
				begin
				select	max(b.nr_sequencia),
					max(b.ds_teste)
				into STRICT	nr_sequencia_w,
					ds_teste_w
				from	teste_software b,
					teste_caso_teste a
				where	b.nr_sequencia = a.nr_seq_teste
				and	a.nr_seq_apres = nr_seq_apres_p
				and	a.nr_sequencia <> nr_sequencia_p
				and	b.nr_sequencia = nr_seq_teste_w;

				if (coalesce(nr_sequencia_w,0) > 0) then
					ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281191) || nr_seq_apres_p || WHEB_MENSAGEM_PCK.get_texto(281193) || ds_teste_w || '".';
				end if;
				end;
			end if;
			end;
		end if;
		end;
	end if;
	end;
elsif (ie_opcao_p = 'I') then
	begin
	select	max(b.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	teste_caso_teste b,
		teste_caso_teste_item a
	where	b.nr_sequencia = a.nr_seq_caso_teste
	and	b.nr_sequencia = nr_seq_superior_p
	and	a.nr_seq_apres = nr_seq_apres_p
	and	a.nr_sequencia <> nr_sequencia_p;

	if (coalesce(nr_sequencia_w,0) > 0) then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281191) || nr_seq_apres_p || WHEB_MENSAGEM_PCK.get_texto(281194);
	end if;
	end;
elsif (ie_opcao_p = 'A') then
	begin
	select	max(b.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	teste_caso_teste_item b,
		teste_caso_teste_it_acao a
	where	b.nr_sequencia = a.nr_seq_item
	and	b.nr_sequencia = nr_seq_superior_p
	and	a.nr_seq_apres = nr_seq_apres_p
	and	a.nr_sequencia <> nr_sequencia_p;

	if (coalesce(nr_sequencia_w,0) > 0) then
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(281191) || nr_seq_apres_p || WHEB_MENSAGEM_PCK.get_texto(281194);
	end if;
	end;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
