CREATE OR REPLACE PROCEDURE consiste_se_agenda_unica ( nr_seq_agenda_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, ie_status_agenda_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

					
cd_pessoa_fisica_w		varchar(10);
nm_pessoa_fisica_w		varchar(50);
ds_agenda_w			varchar(50);
ds_status_w			varchar(50);
hr_inicio_w			varchar(21);
ie_consiste_w		varchar(01);


BEGIN

ie_consiste_w := obter_param_usuario(871, 142, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_w);

select	coalesce(max(a.cd_pessoa_fisica),'0')
into STRICT	cd_pessoa_fisica_w
from	agenda g,
	agenda_paciente a
where	g.cd_agenda		= a.cd_agenda
and	a.cd_agenda 		= cd_agenda_p
and	trunc(a.dt_agenda) 		= trunc(dt_agenda_p)
and	a.hr_inicio		= hr_inicio_p
and	((ie_consiste_w = 'N' AND a.ie_status_agenda	= ie_status_agenda_p) or
	 ((ie_consiste_w = 'S') and (a.ie_status_agenda not in ('C', 'L', 'B'))))
and	((a.nr_sequencia	<> nr_seq_agenda_p) or (nr_seq_agenda_p = 0))
and	g.cd_tipo_agenda	= 1;

if (cd_pessoa_fisica_w <> '0') then
	
	nm_pessoa_fisica_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,50);
	ds_agenda_w		:= substr(obter_nome_agenda(cd_agenda_p),1,50);
	ds_status_w		:= substr(obter_valor_dominio(83, ie_status_agenda_p),1,50);
	hr_inicio_w		:= substr(to_char(hr_inicio_p,'dd/mm/yyyy hh24:mi'),1,21);
	
	CALL Wheb_mensagem_pck.exibir_mensagem_abort( 262575 , 'NM_PESSOA_FISICA_W='||nm_pessoa_fisica_w||';HR_INICIO_W='||hr_inicio_w||';DS_AGENDA_W='||ds_agenda_w||';DS_STATUS_W='||ds_status_w);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
