CREATE OR REPLACE PROCEDURE consiste_se_leito_isol_agrup ( cd_setor_aten_origem_p bigint, cd_unidade_b_dest_p text, cd_unidade_c_dest_p text, cd_unidade_b_orig_p text, cd_unidade_c_orig_p text, cd_setor_destino_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


nr_seq_agrupamento_w	bigint;
ie_isolado_w		varchar(1);
ie_status_unidade_w	varchar(1);


BEGIN

select	max(ie_status_unidade)
into STRICT	ie_status_unidade_w
from	unidade_atendimento
where 	cd_setor_atendimento = cd_setor_aten_origem_p
and	cd_unidade_basica    = cd_unidade_b_orig_p
and	cd_unidade_compl     = cd_unidade_c_orig_p;

if (ie_status_unidade_w = 'O') then

	select  max(nr_agrupamento)
	into STRICT	nr_seq_agrupamento_w
	from	unidade_atendimento
	where	cd_setor_atendimento = cd_setor_destino_p
	and	cd_unidade_basica    = cd_unidade_b_dest_p
	and	cd_unidade_compl     = cd_unidade_c_dest_p;

	if (nr_seq_agrupamento_w IS NOT NULL AND nr_seq_agrupamento_w::text <> '') then

		select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_isolado_w
		from	unidade_atendimento
		where	cd_setor_atendimento = cd_setor_destino_p
		and	nr_agrupamento       = nr_seq_agrupamento_w
		and	ie_status_unidade    = 'O';

		if (ie_isolado_w = 'N') then
			ds_retorno_p	:= wheb_mensagem_pck.get_texto(795990);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
