CREATE OR REPLACE PROCEDURE consiste_se_presc_transcricao (nr_sequencia_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

ie_status_w		varchar(15);
dt_transcricao_w	timestamp;
ie_acao_transcricao_w	varchar(1);


BEGIN

ds_erro_p:= '';

select	max(a.ie_status),
	max(a.dt_transcricao),
	max(b.ie_acao_transcricao)
into STRICT	ie_status_w,
	dt_transcricao_w,
	ie_acao_transcricao_w
from	transcricao_prescricao a,
	classificacao_transcricao b
where	a.nr_seq_classificacao 	= b.nr_sequencia
and 	a.nr_sequencia 		= nr_sequencia_p;

if (ie_status_w = 'S') and (coalesce(dt_transcricao_w::text, '') = '') and (ie_acao_transcricao_w = 'R') then
	CALL Atualizar_Inicio_Transcricao(nr_sequencia_p,nm_usuario_p,'S');
else
	ds_erro_p := wheb_mensagem_pck.get_texto(278080);
end if;

end;
