SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_solic_prontuario ( nr_sequencia_p bigint, nr_prontuario_p bigint, cd_setor_atendimento_p bigint, cd_agenda_p bigint, dt_solicitacao_p timestamp, cd_estabelecimento_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_existe_w	bigint;
cd_agenda_w	bigint;


BEGIN

cd_agenda_w	:= coalesce(cd_agenda_p,0);

select	count(*)
into STRICT	qt_existe_w
from	same_cpi_prontuario
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_prontuario		= nr_prontuario_p;

if (qt_existe_w = 0) then
	ds_retorno_p	:= obter_desc_expressao(732241);--'O numero de prontuario informado nao existe no cadastro.';
end if;

select	count(*)
into STRICT	qt_existe_w
from	same_cpi_solic
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_setor_atendimento	= cd_setor_atendimento_p
and	nr_prontuario		= nr_prontuario_p
and	nr_sequencia		<> nr_sequencia_p
and	ie_status_solic		= 'A'
and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_desejada)	= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_solicitacao_p)
and	((cd_agenda		= cd_agenda_w) or ((coalesce(cd_agenda::text, '') = '') and (cd_agenda_w = 0)));

if (qt_existe_w <> 0) then
	ds_retorno_p	:= obter_desc_expressao(732239);
--'Ja existe uma solicitacao para este numero de prontuario neste local e nesta data.';
end if;

select	count(*)
into STRICT	qt_existe_w
from	same_cpi_emp
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_setor_atendimento	= cd_setor_atendimento_p
and	nr_prontuario		= nr_prontuario_p
and	coalesce(dt_devolucao::text, '') = ''
and	((cd_agenda		= cd_agenda_w) or ((coalesce(cd_agenda::text, '') = '') and (cd_agenda_w = 0)))
and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_solicitacao_p)	= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());

if (qt_existe_w <> 0) then
	ds_retorno_p	:= 'N?o e permitido fazer solicitac?o para esse prontuario. O mesmo ja esta no local solicitante.';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_solic_prontuario ( nr_sequencia_p bigint, nr_prontuario_p bigint, cd_setor_atendimento_p bigint, cd_agenda_p bigint, dt_solicitacao_p timestamp, cd_estabelecimento_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

