SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_sup_int_pj ( nr_sequencia_p bigint, cd_cnpj_p text, cd_estab_p bigint, cd_municipio_ibge_p text, cd_pf_resp_tecnico_p text, cd_cnes_p bigint) AS $body$
DECLARE


qt_existe_w			bigint;


BEGIN

delete from sup_int_pj_consist
where nr_sequencia = nr_sequencia_p;

if (length(cd_cnpj_p) <> 14) then
	CALL grava_sup_int_pj_consist(
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(306327)); --'O CNPJ não possui 14 caracteres'
end if;

select	count(*)
into STRICT	qt_existe_w
from	estabelecimento
where	cd_estabelecimento	= cd_estab_p
and	ie_situacao		= 'A';

if (qt_existe_w = 0) then
	CALL grava_sup_int_pj_consist(
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(306328));  --'O estabelecimento informado não existe no Tasy, ou está inativo.'
end if;

select	count(*)
into STRICT	qt_existe_w
from	pls_cnae
where	nr_sequencia	= cd_cnes_p;

if (qt_existe_w = 0) and (cd_cnes_p IS NOT NULL AND cd_cnes_p::text <> '') then
	CALL grava_sup_int_pj_consist(
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(306329)); --'O CNAE informado não existe no Tasy.'
end if;

select	count(*)
into STRICT	qt_existe_w
from	sus_municipio
where	cd_municipio_ibge	= cd_municipio_ibge_p;

if (qt_existe_w = 0) and (cd_municipio_ibge_p IS NOT NULL AND cd_municipio_ibge_p::text <> '') then
	CALL grava_sup_int_pj_consist(
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(306330));  --'O município IBGE informado não existe no Tasy.'
end if;

select	count(*)
into STRICT	qt_existe_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pf_resp_tecnico_p;

if (qt_existe_w = 0) and (cd_pf_resp_tecnico_p IS NOT NULL AND cd_pf_resp_tecnico_p::text <> '') then
	CALL grava_sup_int_pj_consist(
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(306331));  --'A pessoa responsável informada nãop está cadastrada no Tasy.'
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
