SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_susp_prescr_mat_hor ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ie_mat_hor_disp_farm_w	char(1);
ie_susp_mat_exec_w		char(1);
ds_horario_w			prescr_mat_hor.ds_horario%type;


BEGIN
ds_erro_p	:= '';

-- Verifica o material foi dispensado pela farmácia
select	max(ds_horario),
		CASE WHEN coalesce(max(dt_fim_horario)::text, '') = '' THEN  'N'  ELSE 'S' END
into STRICT	ds_horario_w,
		ie_mat_hor_disp_farm_w
from	prescr_mat_hor
where	nr_sequencia	= nr_sequencia_p
and		nr_prescricao	= nr_prescricao_p
and		Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

if (ie_mat_hor_disp_farm_w = 'S') then
	ie_susp_mat_exec_w := obter_param_usuario(924, 57, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_susp_mat_exec_w);

	-- Caso tenha sido dispensado e o parâmetro 57 estiver habilitado não deixa suspender
	if (ie_susp_mat_exec_w = 'N') then
		-- O horário das #@DS_HORARIO_P#@ não pode ser suspenso, pois já foi atendido pela farmácia. Parâmetro[57].
		ds_erro_p	:=  wheb_mensagem_pck.get_texto(278353, 'DS_HORARIO_P=' || ds_horario_w);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
