SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_sus_compat_proc_adic (nr_seq_agenda_p bigint, nr_cirurgia_p bigint, cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_procedimento_adic_w		bigint;
ie_origem_proced_adic_w		bigint;
cd_procedimento_princ_w		bigint;
ie_origem_proced_princ_w	bigint;
ie_permite_w			varchar(15);


BEGIN
ds_retorno_p := null;

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (obter_tipo_convenio(cd_convenio_p) = 3) then
	if (coalesce(nr_seq_agenda_p,0) > 0) then
		select 	cd_procedimento,
			ie_origem_proced
		into STRICT	cd_procedimento_princ_w,
			ie_origem_proced_princ_w
		from 	agenda_paciente
		where 	nr_sequencia = nr_seq_agenda_p;
	else
		select 	cd_procedimento_princ,
			ie_origem_proced
		into STRICT	cd_procedimento_princ_w,
			ie_origem_proced_princ_w
		from 	cirurgia
		where 	nr_cirurgia = nr_cirurgia_p;
	end if;


	--Verifica se o procedimento adicional que está sendo inserido é compatível com o principal
	if (ie_origem_proced_p = 7) and (ie_origem_proced_princ_w = 7) then
		select	substr(coalesce(max(sus_verifica_se_compat_proc(cd_procedimento_p,ie_origem_proced_p,cd_procedimento_princ_w)),'S'),1,15)
		into STRICT	ie_permite_w
		;
		if (ie_permite_w = 'N') then

			--Não é possível alterar o convênio para SUS. Os procedimentos adicionais são incompatíveis com este procedimento.
			--É necessário realizar a exclusão dos procedimentos adicionais para realizar a troca do convênio/procedimento¿.
			ds_retorno_p := wheb_mensagem_pck.get_texto(338965, null);
		end if;
	end if;

	--Verifica se o procedimento principal é compatível com o adicional que está sendo inserido
	if (coalesce(ds_retorno_p::text, '') = '') and (ie_origem_proced_p = 7) and (ie_origem_proced_princ_w = 7) then
		select	substr(coalesce(max(sus_verifica_se_compat_proc(cd_procedimento_princ_w,ie_origem_proced_princ_w,cd_procedimento_p)),'S'),1,15)
		into STRICT	ie_permite_w
		;
		if (ie_permite_w = 'N') then

			--Não é possível alterar o convênio para SUS. Os procedimentos adicionais são incompatíveis com este procedimento.
			--É necessário realizar a exclusão dos procedimentos adicionais para realizar a troca do convênio/procedimento¿.
			ds_retorno_p := wheb_mensagem_pck.get_texto(338965, null);
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
