CREATE OR REPLACE PROCEDURE consiste_tipo_acomodacao ( nr_atendimento_p bigint, ds_consistencia_p INOUT text ) AS $body$
DECLARE

 
 
cd_nivel_acomodacao_wW	smallint;
cd_nivel_acomodacao_w	smallint;


BEGIN 
 
select	coalesce(max(a.cd_nivel_acomodacao),99) 
into STRICT	cd_nivel_acomodacao_w 
from	tipo_acomodacao a, 
	atend_categoria_convenio b 
where	a.cd_tipo_acomodacao 	= b.cd_tipo_acomodacao 
and	b.nr_seq_interno	= obter_atecaco_atendimento(nr_atendimento_p);
 
select	coalesce(max(a.cd_nivel_acomodacao),99) 
into STRICT	cd_nivel_acomodacao_wW 
from	tipo_acomodacao a, 
	atend_paciente_unidade b 
where	a.cd_tipo_acomodacao 	= b.cd_tipo_acomodacao 
and	b.nr_seq_interno	= obter_atepacu_paciente(nr_atendimento_p,'A');
 
if (cd_nivel_acomodacao_w > cd_nivel_acomodacao_ww) then 
	--ds_consistencia_p := 'O nível do tipo de acomodação do convênio é superior a de que o paciente esta. Deseja bloquear os demais leitos da unidade?'; 
	ds_consistencia_p := WHEB_MENSAGEM_PCK.get_texto(387377);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
