CREATE OR REPLACE PROCEDURE consiste_tit_aberto_eup_js ( ie_carater_inter_sus_p text, ei_carat_perm_atend_tit_venc_w text, ie_local_verificado_p text, cd_pessoa_fisica_p text, cd_pessoa_resp_p text, cd_empresa_p text, cd_estabelecimento_p text, ie_valor_pend_estab_p text, nm_usuario_p text, ds_msg_erro_p INOUT text, ds_msg_alerta_tit_venc_p INOUT text) AS $body$
DECLARE

 
ds_msg_w		varchar(255);	
cd_estab_rest_w		smallint;				
cd_pessoa_w		varchar(14);
ie_possui_deb_venc_w	varchar(1) := 'N';


BEGIN 
 
if (ie_valor_pend_estab_p = 'S') then 
	cd_estab_rest_w := cd_estabelecimento_p;
else 
	cd_estab_rest_w := 0;
end if;
 
if (ie_local_verificado_p = 'P') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	ie_possui_deb_venc_w := obter_se_debitos_vencidos(cd_pessoa_fisica_p, null, cd_estab_rest_w);
elsif (ie_local_verificado_p = 'R') and (cd_pessoa_resp_p IS NOT NULL AND cd_pessoa_resp_p::text <> '') then	 
	ie_possui_deb_venc_w := obter_se_debitos_vencidos(cd_pessoa_resp_p, null, cd_estab_rest_w);
elsif (ie_local_verificado_p = 'E') and (coalesce(cd_empresa_p,0) > 0) then 
	 
	begin 
		select	cd_cgc 
		into STRICT	cd_pessoa_w 
		from	empresa_referencia 
		where	cd_empresa = cd_empresa_p;
	exception 
	when no_data_found then 
		cd_pessoa_w := null;
	end;
	 
	if (cd_pessoa_w IS NOT NULL AND cd_pessoa_w::text <> '') then 
		ie_possui_deb_venc_w := obter_se_debitos_vencidos(null, cd_pessoa_w, cd_estab_rest_w);
	end if;
end if;
 
if (ie_possui_deb_venc_w = 'S') and (ei_carat_perm_atend_tit_venc_w IS NOT NULL AND ei_carat_perm_atend_tit_venc_w::text <> '') and (ie_carater_inter_sus_p IS NOT NULL AND ie_carater_inter_sus_p::text <> '') and (Obter_Se_Contido_char(ie_carater_inter_sus_p,ei_carat_perm_atend_tit_venc_w) ='S') then 
	ds_msg_alerta_tit_venc_p := substr(obter_texto_tasy(239649, wheb_usuario_pck.get_nr_seq_idioma),1,255);
elsif (ie_possui_deb_venc_w = 'S') then 
	ds_msg_erro_p := substr(obter_texto_tasy(239650, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;
 
commit;
 
end;
$body$
