CREATE OR REPLACE PROCEDURE consiste_tit_lib_cc (nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_centro_custo_w		titulo_pagar_classif.cd_centro_custo%type;
ds_retorno_w			varchar(1) := 'N';

C01 CURSOR FOR /*Buscar todos os centro de custo do t√≠tulo*/
SELECT	cd_centro_custo
from	titulo_pagar_classif
where	nr_titulo  = nr_titulo_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_centro_custo_w;
EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on C01 */
	begin
		select  substr(obter_cc_setor_usuario_lib(nm_usuario_p,cd_estabelecimento_p,cd_centro_custo_w),1,1)
		into STRICT	ds_retorno_w
		;
	end;
end loop;
close C01;

if (ds_retorno_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296118);
end if;

commit;

end;
$body$
