CREATE OR REPLACE PROCEDURE consiste_transf_agenda_pac ( nr_seq_origem_p bigint, nr_seq_destino_p bigint, ds_aviso_regra_qtd_p INOUT text, ds_abort_p INOUT text) AS $body$
DECLARE

						 
cd_medico_origem_w		varchar(10);
cd_convenio_origem_w		integer;
ie_carater_cirurgia_origem_w	varchar(15);
dt_agenda_destino_w		timestamp;
cd_agenda_destino_w		bigint;
cd_turno_destino_w		varchar(1);
ds_erro_w			varchar(255);
ie_somente_aviso_w		varchar(255);
	

BEGIN 
 
select	max(cd_medico), 
	max(cd_convenio), 
	max(ie_carater_cirurgia) 
into STRICT	cd_medico_origem_w, 
	cd_convenio_origem_w, 
	ie_carater_cirurgia_origem_w 
from	agenda_paciente 
where	nr_sequencia = nr_seq_origem_p;
 
select	max(dt_agenda), 
	max(cd_agenda), 
	max(cd_turno) 
into STRICT	dt_agenda_destino_w, 
	cd_agenda_destino_w, 
	cd_turno_destino_w 
from	agenda_paciente 
where	nr_sequencia = nr_seq_destino_p;
 
 
if (coalesce(ds_abort_p::text, '') = '') then 
	SELECT * FROM consiste_regra_qtd_agendamento(	nr_seq_origem_p, cd_medico_origem_w, cd_convenio_origem_w, dt_agenda_destino_w, cd_agenda_destino_w, cd_turno_destino_w, ie_carater_cirurgia_origem_w, 'S', 'N', ds_erro_w, ie_somente_aviso_w) INTO STRICT ds_erro_w, ie_somente_aviso_w;
	 
	if (ie_somente_aviso_w = 'N') then 
		ds_abort_p :=	ds_erro_w;
	elsif (ie_somente_aviso_w = 'S') then 
		ds_aviso_regra_qtd_p :=	ds_erro_w;
	end if;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
