CREATE OR REPLACE PROCEDURE consiste_trans_financ_custodia (nr_seq_cheque_p bigint, nr_seq_deposito_p bigint, ie_acao_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_cheque_w		bigint;
dt_custodia_w		timestamp;
dt_resgate_custodia_w	timestamp;
ds_erro_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_seq_cheque
	from	deposito_cheque
	where	nr_seq_deposito = nr_seq_deposito_p
	and	coalesce(nr_seq_cheque_p,0) = 0
	and	nr_seq_deposito_p > 0
	
union all

	SELECT	nr_seq_cheque_p
	
	where	nr_seq_cheque_p > 0
	and	coalesce(nr_seq_deposito_p,0) = 0;


BEGIN
ds_erro_w := null;

if (nr_seq_cheque_p > 0) and (nr_seq_deposito_p > 0) then
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277402);
elsif (coalesce(nr_seq_cheque_p,0) = 0) and (coalesce(nr_seq_deposito_p,0) = 0) then
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277403);
end if;

if (coalesce(ds_erro_w::text, '') = '') then
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_cheque_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	max(a.dt_custodia),
			max(a.dt_resgate_custodia)
		into STRICT	dt_custodia_w,
			dt_resgate_custodia_w
		from	cheque_cr a
		where	a.nr_seq_cheque	= nr_seq_cheque_w;

		if (ie_acao_p = 28) then
			begin
			if (dt_custodia_w IS NOT NULL AND dt_custodia_w::text <> '') then
				ds_erro_w :=  WHEB_MENSAGEM_PCK.get_texto(277404) || to_char(nr_seq_cheque_w) || WHEB_MENSAGEM_PCK.get_texto(277405);
			end if;
			end;
		elsif (ie_acao_p = 29) then
			begin
			if (coalesce(dt_custodia_w::text, '') = '') then
				ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277404)  || to_char(nr_seq_cheque_w) || WHEB_MENSAGEM_PCK.get_texto(277406);
			end if;

			if (dt_resgate_custodia_w IS NOT NULL AND dt_resgate_custodia_w::text <> '') then
				ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277404)  || to_char(nr_seq_cheque_w) || WHEB_MENSAGEM_PCK.get_texto(277407);
			end if;
			end;
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

ds_erro_p := ds_erro_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
