CREATE OR REPLACE PROCEDURE consiste_unid_med_atc (cd_unid_med_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

begin
select	count(*)
into STRICT	qt_registros_w
from	atc_unid_med
where	cd_unid_med	= cd_unid_med_p;
exception
	when others then
	qt_registros_w	:= 0;
end;

if (qt_registros_w = 0) and (cd_unid_med_p IS NOT NULL AND cd_unid_med_p::text <> '') then

	insert into atc_unid_med(cd_unid_med,ds_unid_med,ie_situacao,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec)
		values (cd_unid_med_p,cd_unid_med_p,'A',clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p);

end if;

commit;

end;
$body$
