SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_valor_conta_prot ( nr_seq_protocolo_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

 
 
nr_interno_conta_w	bigint;
vl_conta_resumo_w	double precision;
vl_conta_w		double precision;
ds_retorno_w		varchar(255):= '';
ie_diferenca_w		varchar(1):= 'N';
					
C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p 
	order by nr_interno_conta;					
					 

BEGIN 
 
delete 
from 	w_conta_resumo_dif 
where 	nm_usuario = nm_usuario_p 
and 	nr_seq_protocolo = nr_seq_protocolo_p;
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_conta_resumo_w	:= 0;
	vl_conta_w		:= 0;
	 
	select	obter_valor_conta(nr_interno_conta_w, 0) 
	into STRICT	vl_conta_resumo_w 
	;
	 
	select 	sum(vl_item) 
	into STRICT	vl_conta_w 
	from 	protocolo_convenio_item_v4 
	where 	nr_interno_conta = nr_interno_conta_w 
	and	((ie_proc_mat = 2) or (coalesce(nr_seq_proc_pacote::text, '') = '' or nr_sequencia <> nr_seq_proc_pacote));
	 
	if (coalesce(vl_conta_resumo_w,0) <> coalesce(vl_conta_w,0)) then 
		insert into w_conta_resumo_dif(NR_SEQ_PROTOCOLO, 
					DT_ATUALIZACAO, 
					NM_USUARIO, 
					DT_ATUALIZACAO_NREC, 
					NM_USUARIO_NREC, 
					VL_CONTA_RESUMO, 
					VL_CONTA, 
					NR_INTERNO_CONTA) 
				values (nr_seq_protocolo_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					vl_conta_resumo_w, 
					vl_conta_w, 
					nr_interno_conta_w);
		ie_diferenca_w:= 'S';
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
if (ie_diferenca_w = 'S') then 
	ds_retorno_w:= wheb_mensagem_pck.get_texto(299139);
end if;
 
ds_retorno_p:= ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
