SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_acomodacao_categoria ( cd_convenio_p bigint, cd_categoria_p text, cd_tipo_acomod_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

ds_erro_w	varchar(255)	:= '';
qt_aux_w	bigint;
qt_aux2_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_aux_w
from	cat_conv_tipo_acomodacao
where	cd_convenio = cd_convenio_p
and	cd_categoria = cd_categoria_p;

if (qt_aux_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_aux_w
	from	cat_conv_tipo_acomodacao
	where	cd_convenio = cd_convenio_p
	and	cd_categoria = cd_categoria_p
	and	cd_tipo_acomodacao = cd_tipo_acomod_p;

	select	count(*)
	into STRICT	qt_aux2_w
	from	categoria_convenio
	where	cd_convenio		= cd_convenio_p
	and	cd_categoria		= cd_categoria_p
	and	cd_tipo_acomodacao	= cd_tipo_acomod_p;

	if (qt_aux_w = 0) and (qt_aux2_w = 0) then
		ds_erro_w := 	wheb_mensagem_pck.get_texto(278649);
	end if;
	end;
end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
