SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_agendamento_build (nm_usuario_p text, nr_sequencia_p bigint, IE_OPCAO_P text, ds_retorno_p INOUT text) AS $body$
DECLARE

									
qt_registros_w		bigint;
dt_agendamento_w	timestamp;
ie_periodo_w		varchar(2);

retorno_w		varchar(10):= 'N';

/*
S -- Existe agendamento
N - Agenda livre
I - Agenda incompativel
P - Data passada
*/
BEGIN

select	count(*)
into STRICT	qt_registros_w
from	w_agenda_build a
where	a.nr_sequencia = nr_sequencia_p;

if (qt_registros_w > 0) then

	select	CASE WHEN ie_opcao_p='P' THEN clock_timestamp()  ELSE a.dt_agendamento END ,
		CASE WHEN ie_opcao_p='P' THEN 'P'  ELSE a.ie_periodo END
	into STRICT dt_agendamento_w,
		ie_periodo_w
	from	w_agenda_build a
	where	a.nr_sequencia = nr_sequencia_p;

	select	count(*)
	into STRICT	qt_registros_w
	from	w_agenda_build a
	where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	trunc(a.dt_agendamento) = trunc(dt_agendamento_w)
	and	a.ie_status = 'BA'
	and	a.ie_periodo <> 'P'
	and	((a.ie_periodo = ie_periodo_w) OR
		((ie_opcao_p = 'P') and ((((to_char(clock_timestamp(),'hh24'))::numeric  <2 ) and a.ie_periodo = 'A') or
								(((to_char(clock_timestamp(),'hh24'))::numeric  <4 ) and a.ie_periodo = 'M') or 
								(((to_char(clock_timestamp(),'hh24'))::numeric  <10) and a.ie_periodo = 'V') or
								(((to_char(clock_timestamp(),'hh24'))::numeric  <22) and a.ie_periodo = 'B'))));

	if (qt_registros_w > 0) then
		-- Possui agendamento programado
		retorno_w:= 'S';
	end if;
		
	if (trunc(dt_agendamento_w) = trunc(clock_timestamp())) then
		if (((to_char(clock_timestamp(),'hh24'))::numeric  >= 2) and (ie_periodo_w = 'A')) or
		   (((to_char(clock_timestamp(),'hh24'))::numeric  >= 4) and (ie_periodo_w = 'M')) or
		   (((to_char(clock_timestamp(),'hh24'))::numeric  >= 10) and (ie_periodo_w = 'V')) or
		   (((to_char(clock_timestamp(),'hh24'))::numeric  >= 22) and (ie_periodo_w = 'B')) then
			retorno_w:= 'I';
		end if;
	elsif (trunc(dt_agendamento_w) < trunc(clock_timestamp())) then
		retorno_w:= 'P';
	end if;

	commit;
	ds_retorno_p:= retorno_w;
	
end if;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_agendamento_build (nm_usuario_p text, nr_sequencia_p bigint, IE_OPCAO_P text, ds_retorno_p INOUT text) FROM PUBLIC;

