CREATE OR REPLACE PROCEDURE consistir_agendamento_pf_dur (hr_agenda_p timestamp, nr_minuto_duracao_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agendamento_p bigint default null) AS $body$
DECLARE


qt_agendamento_w	bigint;				
				       

BEGIN

if (hr_agenda_p IS NOT NULL AND hr_agenda_p::text <> '') and (nr_minuto_duracao_p IS NOT NULL AND nr_minuto_duracao_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	select	count(*)
	into STRICT	qt_agendamento_w
	from	agenda_paciente b,
		agenda a
	where	a.cd_agenda 	 	= b.cd_agenda
	and	a.cd_tipo_agenda 	= 2
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((b.hr_inicio between hr_agenda_p and hr_agenda_p + (nr_minuto_duracao_p - 1)/1440) or
		(hr_agenda_p between b.hr_inicio and b.hr_inicio + (nr_minuto_duracao - 1)/1440))
	and	ie_status_agenda not in ('C','I')
	and (b.nr_sequencia <> nr_seq_agendamento_p or coalesce(nr_seq_agendamento_p::text, '') = '');

	if (qt_agendamento_w > 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(262560);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
