SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_agenda_exames (cd_agenda_p bigint, nr_sequencia_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

qt_agenda_pac_dia_w		bigint;
ds_erro_w				varchar(255);
ie_permite_pac_hor_w	varchar(1);


BEGIN
ie_permite_pac_hor_w := obter_param_usuario(820, 391, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_pac_hor_w);

if (ie_permite_pac_hor_w = 'N') then

	select 	count(*)
	into STRICT	qt_agenda_pac_dia_w
	from	agenda_paciente
	where	trunc(hr_inicio) 	= trunc(dt_agenda_p)
	and		cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and		cd_agenda 			= cd_agenda_p
	and		nr_sequencia 		<> nr_sequencia_p
	and		ie_status_agenda 	<> 'C';

	ds_erro_w	:= null;

	if (qt_agenda_pac_dia_w > 0) then
		ds_erro_w	:= 	WHEB_MENSAGEM_PCK.get_texto(277920,null);
	end if;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
