CREATE OR REPLACE PROCEDURE consistir_agenda_tasy_convite ( nm_usuario_convidado_p text, nr_seq_agenda_p bigint, dt_agenda_p timestamp, qt_minuto_duracao_p bigint, dt_final_agenda_p timestamp, ds_erro_p INOUT text, ie_erro_p INOUT text) AS $body$
DECLARE


dt_final_agenda_w	timestamp;
ds_erro_w		varchar(255)	:= null;
ie_erro_w		varchar(1)	:= 'N';


BEGIN
if (nm_usuario_convidado_p IS NOT NULL AND nm_usuario_convidado_p::text <> '') then
	begin
	ds_erro_w	:= null;
	ie_erro_w	:= 'N';
	if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'A' END
		into STRICT	ie_erro_w
		from	ausencia_tasy a,
			agenda_tasy b
		where	a.nm_usuario_ausente	= nm_usuario_convidado_p
		and	b.dt_agenda between a.dt_inicio and a.dt_fim
		and	b.nr_sequencia		= nr_seq_agenda_p;

		if (ie_erro_w = 'A') then
			begin
			ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(281901,'NM_USUARIO_CONVIDADO='|| nm_usuario_convidado_p);
			end;
		end if;
		end;
	end if;

	if (ie_erro_w = 'N') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
		begin
		dt_final_agenda_w	:= coalesce(dt_final_agenda_p, dt_agenda_p + dividir(qt_minuto_duracao_p,1440));
		ie_erro_w		:= coalesce(obter_se_compromisso_agenda(dt_agenda_p, nm_usuario_convidado_p, dt_final_agenda_w),'N');
		if (ie_erro_w = 'S') then
			begin
			ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(281902,'NM_USUARIO_CONVIDADO='|| nm_usuario_convidado_p);
			end;
		end if;
		end;
	end if;
	end;
end if;
ds_erro_p	:= ds_erro_w;
ie_erro_p	:= ie_erro_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
