CREATE OR REPLACE PROCEDURE consistir_agenda_tasy_js ( nm_usuario_agenda_p text, nr_seq_agenda_p bigint, dt_agenda_p timestamp, qt_minuto_duracao_p bigint, nr_seq_local_p bigint, ie_consiste_sobrepos_p text, ie_consiste_periodo_ausencia_p text, ie_consiste_local_p text, ds_erro_p INOUT text, ie_erro_p INOUT text) AS $body$
DECLARE


ie_consiste_sobreposicao_w	varchar(1) 	:= 'S';
ds_erro_w					varchar(255);
ie_erro_w					varchar(1)		:= 'N';
nm_usuario_agenda_w			varchar(15);

BEGIN
if (ie_consiste_sobrepos_p = 'S') then
	begin
	if (ie_consiste_local_p = 'S') then
		begin
		if (coalesce(nr_seq_local_p::text, '') = '') then
			begin
			nm_usuario_agenda_w := substr(nm_usuario_agenda_p,1,15);
			end;
		end if;

		ds_erro_w := consistir_duracao_agenda_tasy(
				dt_agenda_p, qt_minuto_duracao_p, nr_seq_local_p, nr_seq_agenda_p, nm_usuario_agenda_w, ds_erro_w);
		end;
	elsif (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') then
		begin
		select	coalesce(ie_consiste_sobreposicao,'S')
		into STRICT	ie_consiste_sobreposicao_w
		from	local_agenda_tasy
		where	nr_sequencia	= nr_seq_local_p;

		if (ie_consiste_sobreposicao_w = 'S') then
			ds_erro_w := consistir_duracao_agenda_tasy(
					dt_agenda_p, qt_minuto_duracao_p, nr_seq_local_p, nr_seq_agenda_p, null, ds_erro_w);
		end if;
		end;
	end if;
	end;
end if;

if (ie_consiste_periodo_ausencia_p = 'N') then
	begin
	ds_erro_w := consistir_hor_ausencia(
			dt_agenda_p, qt_minuto_duracao_p, ds_erro_w, nm_usuario_agenda_p);
	end;
end if;

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	begin
	ie_erro_w := 'A';
	end;
end if;

ds_erro_p	:= ds_erro_w;
ie_erro_p	:= ie_erro_w;
end;
$body$
