CREATE OR REPLACE PROCEDURE consistir_agenda_tasy ( nm_usuario_agenda_p text, nr_seq_agenda_p bigint, dt_agenda_p timestamp, qt_minuto_duracao_p bigint, nr_seq_local_p bigint, ie_consiste_sobrepos_p text, ie_consiste_periodo_ausencia_p text, ds_erro_p INOUT text, ie_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w			varchar(255)	:= null;
ie_erro_w			varchar(1)	:= 'N';
ie_consiste_sobreposicao_w	varchar(1) 	:= 'S';
ie_consiste_sobrepos_local_w	varchar(1);


BEGIN
if (nm_usuario_agenda_p IS NOT NULL AND nm_usuario_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (qt_minuto_duracao_p IS NOT NULL AND qt_minuto_duracao_p::text <> '') then
	begin
	ds_erro_w	:= null;
	ie_erro_w	:= 'N';

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_erro_w
	from	agenda_tasy
	where	nm_usuario_agenda	= nm_usuario_agenda_p
	and	nr_sequencia 		<> nr_seq_agenda_p
	and	dt_agenda		= dt_agenda_p
	and	ie_status		= 'M';

	ie_consiste_sobrepos_local_w := obter_parametro(791, 19, obter_perfil_ativo, ie_consiste_sobrepos_local_w);

	if (ie_erro_w = 'N') then
		begin

		if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') and (ie_consiste_sobrepos_p = 'S') then
			begin

			select	coalesce(ie_consiste_sobreposicao,'S')
			into STRICT	ie_consiste_sobreposicao_w
			from	local_agenda_tasy
			where	nr_sequencia	= nr_seq_local_p;

			if (ie_consiste_sobreposicao_w = 'S') then
				begin

				ds_erro_w := consistir_duracao_agenda_tasy(
					dt_agenda_p, qt_minuto_duracao_p, nr_seq_local_p, nr_seq_agenda_p, null, ds_erro_w);

				if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
					begin
					ie_erro_w := 'A';
					end;
				end if;
				end;
			end if;
			end;
		elsif (coalesce(nr_seq_local_p::text, '') = '') and (ie_consiste_sobrepos_p = 'S') and (ie_consiste_sobrepos_local_w = 'N') and (nm_usuario_agenda_p IS NOT NULL AND nm_usuario_agenda_p::text <> '') then
			begin

			select	coalesce(ie_consiste_sobreposicao,'S')
			into STRICT	ie_consiste_sobreposicao_w
			from	local_agenda_tasy
			where	nr_sequencia	= nr_seq_local_p;

			if (ie_consiste_sobreposicao_w = 'S') then
				begin

				ds_erro_w := consistir_duracao_agenda_tasy(
					dt_agenda_p, qt_minuto_duracao_p, null, nr_seq_agenda_p, nm_usuario_agenda_p, ds_erro_w);

				if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
					begin
					ie_erro_w := 'A';
					end;
				end if;
				end;
			end if;
			end;
		end if;

		if (ie_consiste_periodo_ausencia_p = 'N') then
			begin
			ds_erro_w := consistir_hor_ausencia(
				dt_agenda_p, qt_minuto_duracao_p, ds_erro_w, nm_usuario_agenda_p);

			if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
				begin
				ie_erro_w := 'A';
				end;
			end if;
			end;
		end if;
		end;
	else
		begin
		ds_erro_w	:= wheb_mensagem_pck.get_texto(279705);
		end;
	end if;
	end;
end if;
ds_erro_p	:= ds_erro_w;
ie_erro_p	:= ie_erro_w;
end;
$body$
